import { authLogout, addLoader, removeLoader } from 'stores';
import * as utils from 'utils';

export const getCountriesList = () => (dispatch, getState) => {
  // prettier-ignore
  const { user: { auth }, config: { vars: { endpoint }}} = getState();

  const defaultError = {
    file: 'stores/claims.actions.getCountriesList',
  };

  dispatch(getCountriesListRequest());
  dispatch(addLoader('getCountriesList'));

  return utils.api
    .get({
      token: auth.accessToken,
      endpoint: endpoint.claimService,
      path: 'api/data/gui/country-code',
    })
    .then((response) => utils.api.handleResponse(response))
    .then((data) => {
      dispatch(getCountriesListSuccess(data.data));
      dispatch(removeLoader('getCountriesList'));
      return data.data;
    })
    .catch((err) => {
      dispatch(getCountriesListFailure(err, defaultError));
      dispatch(removeLoader('getCountriesList'));
      utils.api.handleUnauthorized(err, dispatch, authLogout);
      return err;
    });
};

export const getCountriesListRequest = (data) => {
  return {
    type: 'CLAIMS_COUNTRIESLIST_GET_REQUEST',
    payload: data,
  };
};

export const getCountriesListSuccess = (data) => {
  return {
    type: 'CLAIMS_COUNTRIESLIST_GET_SUCCESS',
    payload: data,
  };
};

export const getCountriesListFailure = (data) => {
  return {
    type: 'CLAIMS_COUNTRIESLIST_GET_FAILURE',
    payload: data,
  };
};
