import React from 'react';
import PropTypes from 'prop-types';
import moment from 'moment';

//app
import * as utils from 'utils';
import { Layout, Empty } from 'components';
import styles from './RfiDetails.styles';
import { RowDetails, RfiQueryForm, RfiResponseDms } from 'modules';
import config from 'config';

//mui
import { makeStyles, Typography, Box, Grid, Card, CardHeader, CardContent, Divider } from '@material-ui/core';
// import PictureAsPdfIcon from '@material-ui/icons/PictureAsPdf';

RfiDetailsView.prototype = {
  taskObj: PropTypes.object.isRequired,
  queryCodeList: PropTypes.array.isRequired,
  rfiHistory: PropTypes.array.isRequired,
  handleViewClaimClick: PropTypes.func.isRequired,
};

export function RfiDetailsView({ taskObj, queryCodeList, rfiHistory, handleViewClaimClick }) {
  const classes = makeStyles(styles, { name: 'RfiDetails' })();
  const selectedQueryCode = queryCodeList?.find((eachQry) => eachQry.queryCodeDetails === taskObj.queryCode);

  return (
    <div className={classes.wrapper}>
      <Layout main padding>
        <Card className={classes.root}>
          <div className={classes.taskSnapshot}>
            <CardHeader
              className={classes.cardHeader}
              title={
                <Grid container direction="row">
                  <Grid item>
                    <Typography variant="body2" className={classes.title}>
                      {utils.string.t('claims.processing.claimSnapshot')}
                    </Typography>
                  </Grid>
                </Grid>
              }
            />
            <CardContent variant="body2" className={classes.cardContent}>
              <Box>
                <div className={classes.details}>
                  <Box>
                    <RowDetails
                      title={utils.string.t('claims.processing.taskDetailsLabels.claimRef')}
                      link={taskObj?.processRef}
                      textAlign="right"
                      handleClick={handleViewClaimClick}
                    />
                    <RowDetails
                      title={utils.string.t('claims.processing.taskDetailsLabels.policyRef')}
                      details={taskObj?.policyRef}
                      textAlign="right"
                    />
                  </Box>
                  <Box>
                    <RowDetails
                      title={utils.string.t('claims.processing.taskDetailsLabels.lossDate')}
                      details={taskObj?.lossFromDate && moment(taskObj?.lossFromDate).format(config.ui.format.date.text)}
                      textAlign="right"
                    />
                    <RowDetails
                      title={utils.string.t('claims.processing.taskDetailsLabels.insured')}
                      details={taskObj?.insured}
                      textAlign="right"
                    />
                  </Box>
                  <Box>
                    <RowDetails
                      title={utils.string.t('claims.processing.taskDetailsLabels.lossDetails')}
                      details={taskObj?.lossDetails}
                      textAlign="right"
                    />
                  </Box>
                </div>
              </Box>
            </CardContent>
          </div>
          <CardHeader
            className={classes.cardHeader}
            title={
              <Grid container direction="row">
                <Grid item>
                  <Typography variant="body2" className={classes.title}>
                    {utils.string.t('claims.processing.taskFunction.RfiDetails')}
                  </Typography>
                </Grid>
              </Grid>
            }
          />
          <CardContent variant="body2" className={classes.cardContent}>
            <div className={classes.details}>
              <Box>
                <RowDetails
                  title={utils.string.t('claims.processing.rfiDetailsLabels.from')}
                  details={taskObj?.requestedByFullName}
                  textAlign="right"
                />
                <RowDetails
                  title={utils.string.t('claims.processing.rfiDetailsLabels.responseDueDate')}
                  details={taskObj?.targetDueDate && moment(taskObj.targetDueDate).format(config.ui.format.date.text)}
                  textAlign="right"
                />
                <RowDetails
                  title={utils.string.t('claims.processing.taskDetailsLabels.description')}
                  details={taskObj?.description}
                  textAlign="right"
                />
              </Box>
              <Box>
                <RowDetails
                  title={utils.string.t('claims.processing.rfiDetailsLabels.queryCode')}
                  details={selectedQueryCode?.queryCodeDescription}
                  textAlign="right"
                />
                <RowDetails
                  title={utils.string.t('claims.processing.rfiDetailsLabels.queryId')}
                  details={taskObj?.queryCode}
                  textAlign="right"
                />
              </Box>
            </div>
          </CardContent>
          <Divider />
          <CardHeader
            className={classes.cardHeader}
            title={
              <Grid container direction="row">
                <Grid item>
                  <Typography variant="body2" className={classes.title}>
                    {utils.string.t('claims.processing.taskFunction.rfiHistory')}
                  </Typography>
                </Grid>
              </Grid>
            }
          />
          <Box>
            {utils.generic.isValidArray(rfiHistory, true) ? (
              rfiHistory?.map((rfiReplyData) => (
                <Grid key={rfiReplyData?.caseIncidentNotesID} container className={classes.historyGrid}>
                  <Grid container direction="row" alignItems="center">
                    <Typography variant="body1" className={classes.userName}>
                      {rfiReplyData.assignee}
                    </Typography>
                    <Typography className={classes.queryDate}>
                      {moment(rfiReplyData.queryDateTime).format(config.ui.format.date.slashNumericDateAndTime)}
                    </Typography>
                  </Grid>
                  <Grid container direction="row" className={classes.queryDescription}>
                    <Typography>{rfiReplyData.description}</Typography>
                  </Grid>

                  <RfiResponseDms
                    task={taskObj}
                    taskResponse={rfiReplyData}
                    defaultState={false}
                    viewOptions={{
                      readOnly: true,
                    }}
                  />

                  <Divider />
                </Grid>
              ))
            ) : (
              <div>
                <Empty text={utils.string.t('claims.processing.empty.emptyRfiHistory')} padding />
                <Divider />
              </div>
            )}
          </Box>
          <Box>
            <RfiQueryForm task={taskObj} selectedQueryCode={selectedQueryCode} rfiHistory={rfiHistory} />
          </Box>
        </Card>
      </Layout>
    </div>
  );
}
