import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useSelector } from 'react-redux';

import config from 'config';

// app
import { selectClaimsPolicyInformation } from 'stores';
import styles from './ClaimsManageDocuments.styles';
import { DmsSearch, Accordion, FormCheckbox, TableHead, TableCell, Button, PopoverMenu, FilterBar, Tooltip, Skeleton } from 'components';
import { RegisterNewLossFixedBottomBar, DmsDocDetailsTooltip } from 'modules';
import * as utils from 'utils';
import * as constants from 'consts';
import { makeStyles, Grid, Box, Typography, Checkbox, FormControlLabel, Table, TableBody, TableRow } from '@material-ui/core';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import DescriptionOutlinedIcon from '@material-ui/icons/DescriptionOutlined';

ClaimsManageDocumentsView.propTypes = {
  isAllStepsCompleted: PropTypes.bool.isRequired,
  activeStep: PropTypes.number.isRequired,
  lastStep: PropTypes.bool.isRequired,
  handleCancel: PropTypes.func.isRequired,
  handleFinish: PropTypes.func.isRequired,
  handleNext: PropTypes.func.isRequired,
  handleBack: PropTypes.func.isRequired,
  handleSave: PropTypes.func.isRequired,
  handleFormStatus: PropTypes.func.isRequired,
  sendDocumentToGXB: PropTypes.func,
  selectAll: PropTypes.bool,
  setSelectAll: PropTypes.func,
  nestedClasses: PropTypes.shape({
    root: PropTypes.string,
  }),
  isDmsFileViewGridDataLoading: PropTypes.bool,
  handlers: PropTypes.shape({
    viewDocLauncher: PropTypes.func.isRequired,
  }).isRequired,
};

export function ClaimsManageDocumentsView({
  activeStep,
  isAllStepsCompleted,
  handleBack,
  handleNext,
  index,
  isFormsEdited,
  handleSave,
  col,
  fields,
  formControls,
  popoverActions,
  searchFields,
  searchActions,
  claimDocuments,
  lossDocuments,
  updateClaimFileListAfterLinking,
  sendDocumentToGXB,
  selectAll,
  setSelectAll,
  handleFormStatus,
  handlers,
  isDmsFileViewGridDataLoading,
}) {
  const classes = makeStyles(styles, { name: 'ClaimsManageDocuments' })();

  const policyInformation = useSelector(selectClaimsPolicyInformation);

  const [isLossAccordionExpanded, setLossAccordionExpanded] = useState(true);
  const [isClaimAccordionExpanded, setClaimAccordionExpanded] = useState(true);

  const { control, register, watch, setValue } = formControls;

  const dms = {
    context: constants.DMS_CONTEXT_POLICY,
    source: policyInformation?.sourceId,
    refData: { ...policyInformation },
    isFnolDmsSearch: true,
    updateClaimFileListAfterLinking: updateClaimFileListAfterLinking,
    documentTypeKey: constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.claim,
  };
  const formValues = watch();
  const allFields = fields?.filter((field) => field?.type === 'checkbox')?.map((field) => field?.name);
  const selectedGroups = fields?.filter((item) => !!item?.defaultValue === true)?.map((item) => item?.name) || [];
  const [selectedGxbDocs, setSelectedGxbDocs] = useState(!utils.generic.isInvalidOrEmptyArray(selectedGroups) ? selectedGroups : []);
  const checkedDocs = (e, docs) => {
    e.stopPropagation();
    let checkedDocument = docs?.checked ? [...selectedGxbDocs, docs?.id] : selectedGxbDocs?.filter((item) => item !== docs?.id);
    let allFieldsSelected = checkedDocument?.length === allFields?.length;
    setSelectedGxbDocs(checkedDocument);
    setSelectAll(allFieldsSelected);
    handleFormStatus();
  };
  const updateSelectAll = (status) => {
    allFields.forEach((grp) => setValue(grp, status));
    setSelectAll(status);
    handleFormStatus();
  };
  const SaveHandle = () => {
    async function sendDocument() {
      sendDocumentToGXB(formValues);
      await handleSave(index);
    }
    sendDocument();
  };

  const nextHandle = () => {
    async function sendDocument() {
      sendDocumentToGXB(formValues);
      await handleNext(index);
    }
    sendDocument();
  };

  const tableGridView = (documents) => {
    return (
      <Table>
        <TableBody>
          {isDmsFileViewGridDataLoading ? (
            <TableRow>
              <TableCell colSpan={col.length}>
                <Skeleton height={40} animation="wave" displayNumber={4} />
              </TableCell>
            </TableRow>
          ) : (
            documents?.map((doc, ind) => {
              const updatedPopOverActions = doc?.isLinkedToMultipleContexts
                ? [...popoverActions].map((action) => {
                    const isDeleteAction = action.id === 'delete';
                    return isDeleteAction ? { ...action, disabled: true } : action;
                  })
                : [...popoverActions];
              const actionDescId = doc?.documentId.toString();
              return (
                <TableRow key={ind} hover className={classes.docViewCursor} onClick={(e) => handlers.viewDocLauncher(e, doc)}>
                  <TableCell className={classes.checkBoxCell}>
                    <FormCheckbox
                      name={actionDescId}
                      {...utils.form.getFieldProps(fields, actionDescId)}
                      control={control}
                      register={register}
                      watch={watch}
                      muiComponentProps={{
                        onClick: (e) => checkedDocs(e, { id: e.target.name, checked: e.target.checked }),
                      }}
                    />
                  </TableCell>
                  <TableCell compact minimal className={classes.tableCell}>
                    <Tooltip title={<DmsDocDetailsTooltip data={doc} />} placement={'bottom'} arrow>
                      {doc?.documentName}
                    </Tooltip>
                  </TableCell>
                  <TableCell compact minimal className={classes.tableCell}>
                    {doc?.createdByName}
                  </TableCell>
                  <TableCell compact minimal className={classes.tableCell}>
                    {utils.string.t('format.date', { value: { date: doc?.createdDate, format: config.ui.format.date.text } })}
                  </TableCell>
                  <TableCell>
                    <PopoverMenu
                      id="view-menu-list"
                      items={updatedPopOverActions}
                      data={{ doc }}
                      anchorOrigin={{
                        vertical: 'bottom',
                        horizontal: 'left',
                      }}
                      transformOrigin={{
                        vertical: 'top',
                        horizontal: 'left',
                      }}
                    />
                  </TableCell>
                </TableRow>
              );
            })
          )}
        </TableBody>
      </Table>
    );
  };

  const referenceId = policyInformation?.policyRef;

  return (
    <Box overflow="hidden" display="flex" flexDirection="column">
      <Box flex="1 1 auto" className={classes.container}>
        <Box mt={3}>
          <Grid container direction="row" spacing={4}>
            <Grid item xs={12} sm={6}>
              <DmsSearch {...dms} referenceId={referenceId} isAutoSearchScreen={true} handleFormStatus={handleFormStatus} />
            </Grid>

            <Grid item xs={12} sm={6}>
              <Box mt={1} ml={2}>
                <Grid className={classes.gridFlex} alignItems="center">
                  <Box display="flex" alignItems="center">
                    <Box className={classes.icon}>
                      <DescriptionOutlinedIcon />
                    </Box>

                    <Typography variant="body2" className={classes.title}>
                      {utils.string.t('claims.manageDocumentLabels.claimFile')}
                    </Typography>
                  </Box>
                  <Box width={'50%'} marginLeft="auto">
                    {' '}
                    <FilterBar id="userFilter" fields={searchFields} actions={searchActions} />{' '}
                  </Box>
                </Grid>

                <Table size="large">
                  <TableHead columns={col} nestedClasses={{ tableHead: classes.tableHead }} />
                  <TableBody>
                    <TableRow>
                      <TableCell colSpan={4}>
                        <Accordion
                          icon={false}
                          expanded={isLossAccordionExpanded}
                          defaultHeader={false}
                          actions={[
                            {
                              id: 'manageLossDocs',
                              text: utils.string.t('claims.manageDocumentLabels.loss'),
                              icon: KeyboardArrowUpIcon,
                              iconPosition: 'left',
                              color: 'primary',
                              onClick: () => {
                                setLossAccordionExpanded(!isLossAccordionExpanded);
                              },
                              nestedClasses: {
                                link: classes.toggleBtnLink,
                                icon: classes.toggleBtnIcon,
                              },
                            },
                          ]}
                        >
                          {tableGridView(lossDocuments)}
                        </Accordion>
                      </TableCell>
                    </TableRow>

                    <TableRow>
                      <TableCell colSpan={4}>
                        <Accordion
                          icon={false}
                          expanded={isClaimAccordionExpanded}
                          defaultHeader={false}
                          actions={[
                            {
                              id: 'manageLossDocs',
                              text: utils.string.t('claims.manageDocumentLabels.claim'),
                              icon: KeyboardArrowUpIcon,
                              iconPosition: 'left',
                              color: 'primary',
                              onClick: () => {
                                setClaimAccordionExpanded(!isClaimAccordionExpanded);
                              },
                              nestedClasses: {
                                link: classes.toggleBtnLink,
                                icon: classes.toggleBtnIcon,
                              },
                            },
                          ]}
                        >
                          {tableGridView(claimDocuments)}
                        </Accordion>
                      </TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
                
                {false &&   //adding false temporary to hide the Select All 
                  <Box mt={10} ml={5}>
                    <Button
                      color="primary"
                      size="xsmall"
                      variant="outlined"
                      disabled={false}
                      text={<FormControlLabel control={<Checkbox checked={selectAll} />} label="SELECT ALL" />}
                      data-testid="bulk-button"
                      onClick={() => {
                        updateSelectAll(!selectAll);
                      }}
                    />
                  </Box>
                }
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Box>

      <Box flex="0 1 auto">
        <RegisterNewLossFixedBottomBar
          activeStep={activeStep}
          isAllStepsCompleted={isAllStepsCompleted}
          handleBack={() => {
            handleBack(index);
          }}
          handleSave={() => {
            SaveHandle();
          }}
          handleNextSubmit={() => nextHandle()}
          save={true}
        />
      </Box>
    </Box>
  );
}
