import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import get from 'lodash/get';
import isEmpty from 'lodash/isEmpty';

//app
import { TreeView } from 'components';
import {
  selectDmsDocDetails,
  selectClaimData,
  selectLossId,
  selectClaimAssociateWithLoss,
  getClaimsAssociateWithLoss,
  getViewTableDocuments,
  selectDmsWidgetClaimDocs
} from 'stores';
import config from 'config';
import * as utils from 'utils';

export default function DmsWidget() {
  const dispatch = useDispatch();
  const lossInformation = useSelector(selectLossId);
  const savedDmsDocList = useSelector(selectDmsDocDetails);
  const claimData = useSelector(selectClaimData);
  const associatedClaims = useSelector(selectClaimAssociateWithLoss);
  const dmsWidgetClaimDocs = useSelector(selectDmsWidgetClaimDocs);
  const [filteredData, setFilteredData] = useState([]);
  const currentLossDetailId = get(lossInformation, 'lossDetailID');
  const currentLossRef = get(lossInformation, 'lossRef');
  const lossDocDetails = get(savedDmsDocList, 'lossDocDetails');
  const claimsDocDetails = get(savedDmsDocList, 'claimsDocDetails');
  const currentClaimRef = get(claimData, 'claimRef') || get(claimData, 'claimReference');

  useEffect(() => {
    if (currentLossDetailId) {
      dispatch(getClaimsAssociateWithLoss(currentLossDetailId))
    }
  }, [currentLossDetailId]) // eslint-disable-line react-hooks/exhaustive-deps

  const claimRefsArray = associatedClaims?.map((item) => item.claimReference)?.filter((claimref) => claimref !== currentClaimRef);

  const createSortedDocs = (docs) => (
    docs?.sort((doc1, doc2) => new Date(doc1?.createdDate) - new Date(doc2?.createdDate))
  )

  const lossDocuments = createSortedDocs(lossDocDetails?.map((doc) => {
    return {
      ...doc,
      id: doc.documentId,
      label: doc.documentName,
      labelInfo: utils.string.t('format.date', { value: { date: doc?.createdDate, format: config.ui.format.date.text } })
    }
  }));

  const currentClaimDocuments = createSortedDocs(claimsDocDetails?.map((doc) => {
    return {
      ...doc,
      id: doc.documentId,
      label: doc.documentName,
      labelInfo: utils.string.t('format.date', { value: { date: doc?.createdDate, format: config.ui.format.date.text } })
    }
  }));

  const openedChild = [
    ...lossDocuments,
    ...(currentClaimRef ? [{
      id: 'child1',
      label: currentClaimDocuments?.length ? `Claim ${currentClaimRef}` : '',
      labelInfo: '',
      children: currentClaimDocuments
    }] : [])
  ]

  const closedChild = claimRefsArray?.[0]?.claimId
    ? claimRefsArray?.map((item) => {
      return {
        id: item, label: item ? `Claim ${item}` : '',
        labelInfo: '',
        children: createSortedDocs([dmsWidgetClaimDocs[item]])
      }
    })
    : [];

  const rootChildren = [...openedChild, ...closedChild];

  const data = {
    id: 'root',
    label: rootChildren?.length ? `Loss Reference ${currentLossRef}` : '',
    labelInfo: '',
    children: rootChildren
  };

  let arr = [];

  data?.children?.forEach(child => {
    if (child.labelInfo) {
      arr.push(child)
    } else {
      child?.children?.forEach(child => {
        if (child) {
          arr.push(child)
        }
      })
    }
  });
  
  const handleSelect = (_, nodeIds) => {
    if (nodeIds !== 'root' && nodeIds !== 'child1') {
      dispatch(getViewTableDocuments({ referenceId: nodeIds, sectionType: 'Claim', isFromDmsWidget: true }));
    }
  }

  const submitSearch = (query) => {
    const filteredData = createSortedDocs(arr?.filter(({
      documentName, updatedDate, createdDate, sectionType, referenceId, documentTypeDescription, createdByName
    }) => !isEmpty([
      documentName, updatedDate, createdDate, sectionType, referenceId, documentTypeDescription, createdByName
    ]?.find(item => item?.toLowerCase()?.includes(query)))));
    setFilteredData(filteredData);
  }

  const resetSearch = () => {
    setFilteredData([]);
  }

  return (
    <TreeView
      handleAccordionClick={handleSelect}
      treeData={data}
      rootRef={currentLossRef}
      widgetTitle={utils.string.t('dms.view.documents.title')}
      columns={[
        { header: utils.string.t('dms.upload.file'), flex: '0.8', ml: '8px' },
        { header: utils.string.t('dms.upload.uploadedOn'), flex: '0.375' }
      ]}
      defaultExpanded={['child1', 'root']}
      filteredData={filteredData}
      handlers={{
        submitSearch,
        resetSearch
      }}
    />
  )
}
