const styles = (theme) => ({
  container: {
    overflowY: 'auto',
    paddingLeft: 32,
    paddingRight: 32,
  },
  claimButton: {
    marginTop: theme.spacing(4),
  },
  title: {
    marginTop: theme.spacing(6),
    paddingBottom: theme.spacing(0.5),
    textTransform: 'uppercase',
    fontWeight: theme.typography.fontWeightBold,
    fontSize: theme.typography.pxToRem(14),
  },
  tableHead: {
    fontWeight: theme.typography.fontWeightBold,
  },
  infoCardContainer: {
    display: 'flex',
    flexFlow: 'row wrap',
    width: '100%',
    alignContent: 'space-around',
    borderTop: '3px dotted rgba(0, 0, 0, 0.12)',
  },
  infoCard: {
    width: '30%',
    margin: theme.spacing(1.25),
  },
  infoCardTitle: {
    fontWeight: 600,
    textAlign: 'left',
    margin: theme.spacing(0, 0, 1.25, 0),
    color: 'rgba(0, 0, 0, 0.87)!important',
  },
  infoCardValue: {
    textAlign: 'left',
    margin: theme.spacing(0, 0, 0, 0.25),
    color: 'inherit!important',
    fontWeight: 400,
  },
  underWritingInfoCardContainer: {
    display: 'flex',
    flexFlow: 'row wrap',
    width: '100%',
    alignContent: 'space-around',
  },
  underWritingContainer: {
    width: '100%',
    borderTop: '3px dotted rgba(0, 0, 0, 0.12)',
  },
  underWritingCardContainer: {
    margin: theme.spacing(3, 0.25),
  },
});

export default styles;
