import React from 'react';
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';

// app
import styles from './RegisterClaimCardInformation.styles';
import * as utils from 'utils';
import {
  FormContainer,
  FormText,
  FormGrid,
  FormLabel,
  FormDate,
  FormAutocompleteMui,
  FormRadio,
  FormCheckbox,
  FormFields,
  FormLegend,
} from 'components';
import { selectClaimData, selectClaimIdFromGrid, selectClaimsPolicyInformation } from 'stores';
import { ClaimsUploadViewSearchDocs } from 'modules';
import * as constants from 'consts';

// mui
import { makeStyles, Box, Grid } from '@material-ui/core';
RegisterClaimCardInformationView.prototype = {
  claimantNames: PropTypes.array.isRequired,
  referralValues: PropTypes.array.isRequired,
  lossQualifiers: PropTypes.object,
  resetKey: PropTypes.number,
  hasClaimRef: PropTypes.bool.isRequired,
  claimsDocsList: PropTypes.array
};
export function RegisterClaimCardInformationView({
  fields,
  claimantNames,
  referralValues,
  resetKey,
  hasClaimRef,
  claimsDocsList
}) {
  const classes = makeStyles(styles, { name: 'EnterClaimCardInformation' })();
  const claimRefId = useSelector(selectClaimData);
  const claimData = useSelector(selectClaimIdFromGrid);
  const policyInformation = useSelector(selectClaimsPolicyInformation);
  const dateValidation = false;
  const defaultValues = utils.form.getInitialValues(fields);
  const validationSchema = utils.form.getValidationSchema(fields);

  const claimForm = useForm({
    defaultValues,
    ...(validationSchema && { resolver: yupResolver(validationSchema) }),
  });

  const { control, errors, setValue } = claimForm;

  const getRefValues = (referralValue) => {
    if (referralValue) {
      const getReferralValue = referralValues.find(
        (item) => item.complexityRulesID === referralValue?.complexityRulesID
      )?.complexityRulesValue;
      const refValuesList = ['Referral Not Required', 'Referral Not Required: Assign to Myself', 'Unsure'];
      const refResponseState = refValuesList.includes(getReferralValue);
      if (refResponseState) {
        setValue('rfiResponse', null);
      }
    } else if (referralValue === null || !referralValue) {
      setValue('rfiResponse', null);
    }
  };

  console.log("claimData ", claimData)

  return (
    <>
      <Box m={3} mb={0}>
        <Grid container direction="row" justify="space-between" alignItems="center">
          <Grid item className={classes.legendContainer}>
            <FormLegend text={utils.string.t('claims.claimInformation.title')} nestedClasses={{ root: classes.formLegend }} />
          </Grid>
          <FormLabel label={`${(claimRefId?.claimReference || claimRefId?.claimRef)} | ${policyInformation.policyRef} | Rolls Royce`} align="left" />
        </Grid>
      </Box>
      <FormContainer data-testid="form-claimsInformation">
        <FormFields type="dialog">
          <FormGrid container spacing={6}>
            <FormGrid item xs={12} sm={4}>
            <FormGrid container>
                <FormGrid item xs={12}>
                  <FormLabel label={utils.string.t('claims.claimInformation.claimRef')} align="left" />
                  <FormLabel label={(claimRefId?.claimReference || claimRefId?.claimRef) ?? utils.string.t('claims.claimInformation.autoGenerated')} nestedClasses={{ root: classes.claimRef }} align="left" />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormDate
                    {...utils.form.getFieldProps(fields, 'fromDate', control)}
                    error={errors.toDate}
                    muiComponentProps={{
                      disabled: dateValidation,
                      fullWidth: true,
                      classes: {
                          root: classes.datepicker,
                        },
                      }}
                  />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormText {...utils.form.getFieldProps(fields, 'location', control)} error={errors.location} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormRadio {...utils.form.getFieldProps(fields, 'adjuster', control)} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'complexity', control)} error={errors.complexity} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormCheckbox {...utils.form.getFieldProps(fields, 'bordereauClaim', control)} error={errors.bordereauClaim} nestedClasses={{ root: classes.checkbox }}/>
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid item xs={12} sm={4}>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormGrid container alignItems="center" spacing={2}>
                        <FormGrid item xs={7}>
                          <FormDate {...utils.form.getFieldProps(fields, 'claimReceivedDate', control)} error={errors.claimReceivedDate} />
                        </FormGrid>
                        <FormGrid item xs={5} nestedClasses={{ root: classes.lossTime }}>
                          <FormText {...utils.form.getFieldProps(fields, 'claimReceivedTime', control)} />
                        </FormGrid>
                      </FormGrid>
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormDate
                        {...utils.form.getFieldProps(fields, 'toDate', control)}
                        error={errors.toDate}
                        muiComponentProps={{
                          disabled: dateValidation,
                          fullWidth: true,
                          classes: {
                            root: classes.datepicker,
                          },
                        }}
                      />
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormText {...utils.form.getFieldProps(fields, 'fgunarrative', control)} error={errors.fgunarrative} />
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'beAdjuster', control)} error={errors.beAdjuster} />
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'rfiResponse', control)} error={errors.rfiResponse} />
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'bordereauPeriod', control)} error={errors.bordereauPeriod} />
                    </FormGrid>
                  </FormGrid>
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid item xs={12} sm={4}>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'claimantName', control)} error={errors.claimantName} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'lossQualifierID', control)} error={errors.lossQualifierID} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'priority', control)} error={errors.priority} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormText {...utils.form.getFieldProps(fields, 'adjusterReference', control)} error={errors.adjusterReference} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'referral', control)} error={errors.referral}
                    callback={(e, data) => {
                      getRefValues(data);
                    }}
                  />
                </FormGrid>
              </FormGrid>
          </FormGrid>
          </FormGrid>
        </FormFields>
      </FormContainer>

      <Box className={classes.claimsContainer}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <ClaimsUploadViewSearchDocs
                refData={claimData}
                refIdName={claimData?.claimRef ? 'claimRef' : 'claimReference'} //added temp condition as claimRef and claimReference keys are inconsistent, need to be fixed permanently later
                dmsContext={constants.DMS_CONTEXT_CLAIM}
                documentTypeKey={constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.type.claims}
                isTabView={false}
                isClaims
                isClaimsUploadDisabled={!claimRefId?.claimRef}
                claimsUploadWarningMsg={!claimRefId?.claimRef ? utils.string.t('claims.claimInformation.dms.fileUploadWarning') : ''}
                claimsSearchDocumentsTxt={utils.string.t('claims.claimInformation.dms.searchDocuments')}
                uploadDocumentsTitle={utils.string.t('claims.claimInformation.dms.uploadDocuments')}
                docList={claimsDocsList}
              />       
            </Grid>
          </Grid>
        </Box>
    
    </>
  );
}
