import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';

import LinkSearchPolicyView from './LinkSearchPolicy.view';
import {
  selectLossId,
  selectLossQualifiers,
  selectCatCodes,
  getPolicyInformation,
  selectClaimsPolicyInformation,
  getClaimantNames,
  selectClaimsPolicyData,
  getClaimDetails,
} from 'stores';

LinkSearchPolicy.propTypes = {
  isAllStepsCompleted: PropTypes.bool.isRequired,
  activeStep: PropTypes.number.isRequired,
  lastStep: PropTypes.bool.isRequired,
  handleCancel: PropTypes.func.isRequired,
  handleFinish: PropTypes.func.isRequired,
  handleNext: PropTypes.func.isRequired,
  handleBack: PropTypes.func.isRequired,
  handleNextSubmit: PropTypes.func,
};

export default function LinkSearchPolicy(props) {
  const dispatch = useDispatch();
  const lossInformation = useSelector(selectLossId);
  const catCodes = useSelector(selectCatCodes);
  const lossQualifiers = useSelector(selectLossQualifiers);
  const policyInformation = useSelector(selectClaimsPolicyInformation);
  const policyData = useSelector(selectClaimsPolicyData);
  const [validation, setValidation] = useState(false);
  const [confirm, setConfirm] = useState(false);
  const [currentContextActive, setCurrentContextActive] = useState(true);
  const hasPolicyRef = Boolean(policyInformation?.policyRef);

  const handleAddClaim = () => {
    dispatch(getClaimantNames());
  };

  useEffect(() => {
    if (policyData?.xbInstanceID) {
      dispatch(getPolicyInformation());
      setConfirm(true);
    }
  }, [policyData]); // eslint-disable-line react-hooks/exhaustive-deps

  const handleSearchNext = () => {
    dispatch(getClaimDetails());
    if (confirm) {
      handleAddClaim();
    }
    props.handleNext();
  };

  const onSaveHandle = () => {
    props.handleSave(props.index);
  }

  return (
    <>
      <LinkSearchPolicyView
        {...props}
        policyInformation={policyInformation}
        handleAddClaim={handleAddClaim}
        lossInformation={lossInformation}
        catCodes={catCodes}
        lossQualifiers={lossQualifiers}
        policyData={policyData}
        handleSearchNext={handleSearchNext}
        confirm={confirm}
        setConfirm={setConfirm}
        hasPolicyRef={hasPolicyRef}
        setCurrentContextActive={setCurrentContextActive}
        currentContextActive={currentContextActive}
        validation={validation}
        setValidation={setValidation}
        onSaveHandle={onSaveHandle}
      />
    </>
  );
}
