  const handleEditClaim = () => {
    async function fetchData() {
      dispatch(
        setClaimData({
          lossId: claimData?.lossDetailID,
          claimId: claimId,
          policyRef: policyInformation?.policyRef,
          policyNumber: policyInformation?.policyRef,
          xbInstanceID: policyInformation?.xbInstanceID,
