        date: '22 Nov 2021',
      },
      {
        id: '6',
        name: '20170606 Initial Advice',
        date: '22 Nov 2021',
      },
      {
        id: '7',
        name: '20170606 Initial Advice',
        date: '22 Nov 2021',
      },
      {
        id: '8',
        name: '20170606 Initial Advice',
        date: '22 Nov 2021',
      },
      {
        id: '9',
        name: 'Claim 04063',
        date: '',
        children: [
          {
            id: '10',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '11',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '12',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '13',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '14',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '15',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
        ],
      },
      {
        id: '16',
        name: 'Claim 5473',
        date: '',
        children: [
          {
            id: '17',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '18',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '19',
            name: '20173453 Attorney',
            date: '12 Sep 2020',
          },
          {
            id: '20',
