import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import { useHistory } from 'react-router';

//app
import { Breadcrumb, Layout, Tabs, PopoverMenu, SectionHeader } from 'components';
import config from 'config';
import { LossDetail, LossAction, ClaimsUploadViewSearchDocs, ClaimsProcessingDmsWidget } from 'modules';
import * as utils from 'utils';
import { DrawerComponent } from 'components';
import styles from './LossDashboard.styles';
import { selectDmsWidgetExpanded } from 'stores';
import * as constants from 'consts';
import { selectClaimAssociateWithLoss, setClaimsStepperControl } from 'stores';

//mui
import { Box, Divider } from '@material-ui/core';
import DescriptionIcon from '@material-ui/icons/Description';
import { makeStyles } from '@material-ui/core';

LossDashboardView.propTypes = {
  tabs: PropTypes.array.isRequired,
  selectedTab: PropTypes.string.isRequired,
  lossSelected: PropTypes.object.isRequired,
  breadcrumbs: PropTypes.arrayOf(
    PropTypes.shape({
      name: PropTypes.string.isRequired,
      label: PropTypes.string.isRequired,
      link: PropTypes.string.isRequired,
    })
  ).isRequired,
  handleSelectTab: PropTypes.func,
};

export function LossDashboardView({ tabs, selectedTab, lossSelected, breadcrumbs, handleSelectTab }) {
  const classes = makeStyles(styles, { name: 'LossDashboard' })();
  const isDmsWidgetExpanded = useSelector(selectDmsWidgetExpanded);
  const dispatch = useDispatch();
  const history = useHistory();
  const claimsAssociateWithLoss = useSelector(selectClaimAssociateWithLoss);

  const editLossHandler = (lossSelected) => {
    const lossRef = lossSelected?.lossRef;
    const checkClaimStatus = utils.generic.isValidArray(claimsAssociateWithLoss, true) ? Boolean(claimsAssociateWithLoss?.find((claim) => claim?.claimStatus !== constants.STATUS_CLAIMS_DRAFT?.toUpperCase())) : false;
    dispatch(setClaimsStepperControl(0));
    history.push({ pathname: `${config.routes.claimsFNOL.newLoss}`, state: { redirectUrl: `${config.routes.claimsFNOL.loss}/${lossRef}`, loss: { isNextDiabled: true, isClaimSubmitted: checkClaimStatus } } });
  };

  const addClaimHandler = (lossSelected) => {
    const lossRef = lossSelected?.lossRef;
    dispatch(setClaimsStepperControl(1));
    const checkClaimStatus = utils.generic.isValidArray(claimsAssociateWithLoss, true) ? Boolean(claimsAssociateWithLoss?.find((claim) => claim?.claimStatus !== constants.STATUS_CLAIMS_DRAFT?.toUpperCase())) : false;
    history.push({ pathname: `${config.routes.claimsFNOL.newLoss}`, state: { redirectUrl: `${config.routes.claimsFNOL.loss}/${lossRef}`, loss: { isNextDiabled: false, isClaimSubmitted: checkClaimStatus } } });
  };

  const createRFIHandler = (lossSelected) => { };

  const rowActions = [
    {
      id: 'editLoss',
      label: utils.string.t('loss.popOverFunctionMenusList.editLoss'),
      callback: () => editLossHandler(lossSelected),
    },
    {
      id: 'Create Claim',
      label: utils.string.t('loss.popOverFunctionMenusList.createClaim'),
      callback: () => addClaimHandler(lossSelected),
    },
    {
      id: 'Create RFI',
      label: utils.string.t('loss.popOverFunctionMenusList.createRFI'),
      callback: () => createRFIHandler(lossSelected),
    },
  ];

  return (
    <>
      <Breadcrumb links={breadcrumbs} />
      <Divider />
      <div
        className={
          classnames(classes.content, {
            [classes.contentShift]: isDmsWidgetExpanded,
          })
        }
      >
        <Layout testid="LossDashboard">
          <Layout main>
            <SectionHeader
              title={utils.string.t('loss.text', {
                claimRef: 1234,
              })}
              icon={DescriptionIcon}
              testid="claim-ref-header"
            >
              <PopoverMenu
                variant="outlined"
                id="loss-functions"
                size="small"
                color="primary"
                text={utils.string.t('loss.popOverFunction')}
                isButton
                anchorOrigin={{
                  vertical: 'bottom',
                  horizontal: 'right',
                }}
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                items={rowActions}
              />
            </SectionHeader>
            <Tabs tabs={tabs} value={selectedTab} onChange={(tabName) => handleSelectTab(tabName)} />
            {selectedTab === 'lossDetail' && <LossDetail lossObj={lossSelected} />}
            {selectedTab === 'lossAction' && <LossAction />}
            {selectedTab === 'lossDocs' && (
              <Box mt={2}>
                <ClaimsUploadViewSearchDocs
                  refData={lossSelected}
                  refIdName={constants.DMS_CONTEXT_LOSS_ID}
                  dmsContext={constants.DMS_CONTEXT_LOSS}
                  documentTypeKey={constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.claim}
                />
              </Box>
            )}
          </Layout>
        </Layout>
      </div>
      <DrawerComponent isDrawerOpen isFromDashboard>
        <ClaimsProcessingDmsWidget isLossDashboard />
      </DrawerComponent>
    </>
  );
}
