import React from 'react';
import { Form } from 'components';
import { withKnobs, object } from '@storybook/addon-knobs';
import * as Yup from 'yup';
import * as utils from 'utils';

export default {
  title: 'Form',
  component: Form,
  decorators: [withKnobs],
};

const getFields = [
  {
    name: 'legend',
    type: 'legend',
    label: 'Form Legend',
  },
  {
    name: 'field-text',
    type: 'text',
    label: 'Text',
    hint: 'Type some text',
    placeholder: 'placeholder',
    value: '',
    validation: Yup.string().max(6).required(),
    muiComponentProps: {
      fullWidth: true,
    },
  },
  {
    name: 'field-textarea',
    type: 'textarea',
    label: 'Textarea',
    hint: 'Type a lot of text',
    placeholder: 'placeholder',
    value: '',
    validation: Yup.string().min(12).required(),
    muiComponentProps: {
      fullWidth: true,
      multiline: true,
      minRows: 3,
      maxRows: 6,
    },
  },
  {
    name: 'field-number',
    type: 'number',
    label: 'Number',
    hint: 'Type any number',
    placeholder: 'ex: 0, 1000, 9.95',
    value: '',
    validation: Yup.number()
      .min(20)
      .nullable()
      .transform((value) => (Number.isNaN(value) ? null : value))
      .required(),
    muiComponentProps: {
      fullWidth: true,
    },
  },
  {
    name: 'field-datepicker',
    type: 'datepicker',
    label: 'Datepicker',
    hint: 'Choose a date',
    placeholder: 'placeholder',
    outputFormat: 'iso', // TODO added on 17/04/2020: check why utils.string.t() doesn't work in Storybook
    value: null,
    muiComponentProps: {
      fullWidth: true,
    },
    validation: Yup.string().nullable().required(),
  },
  {
    name: 'field-select',
    type: 'select',
    label: 'Select',
    hint: 'Select an option',
    value: '__placeholder__',
    options: [
      { label: 'placeholder', value: '__placeholder__', placeholder: true },
      { label: 'yes', value: 'yes' },
      { label: 'no', value: 'no' },
      { label: 'maybe', value: 'maybe' },
      { label: 'sometimes', value: 'sometimes' },
      { label: 'always', value: 'always' },
      { label: 'never', value: 'never' },
    ],
    validation: Yup.string()
      .notOneOf(['__placeholder__'], '${path} is a required field') // eslint-disable-line no-template-curly-in-string
      .required(),
  },
  {
    name: 'field-autocomplete',
    type: 'autocomplete',
    label: 'Autocomplete',
    hint: 'Select a few options',
    placeholder: 'placeholder',
    value: [],
    options: [
      { label: 'yes', value: 'yes' },
      { label: 'no', value: 'no' },
      { label: 'maybe', value: 'maybe' },
      { label: 'sometimes', value: 'sometimes' },
      { label: 'always', value: 'always' },
      { label: 'never', value: 'never' },
    ],
    innerComponentProps: {
      isMulti: true,
    },
    validation: Yup.array().min(2).required(),
  },
  {
    name: 'field-checkbox',
    type: 'checkbox',
    value: false,
    label: 'yes',
  },
  {
    name: 'field-switch',
    type: 'switch',
    title: 'Switch',
    hint: 'Select option(s)',
    options: [
      { label: 'red', value: 'red' },
      { label: 'blue', value: 'blue' },
      { label: 'yellow', value: 'yellow' },
      { label: 'green', value: 'green' },
      { label: 'gold', value: 'gold', disabled: true },
    ],
    value: ['blue'],
    validation: Yup.array().min(2).required(),
  },
  {
    name: 'field-radio',
    type: 'radio',
    label: 'Radio',
    hint: 'Select an option',
    value: '',
    options: [
      { label: 'yes', value: 'yes' },
      { label: 'no', value: 'no' },
    ],
    validation: Yup.string().required(),
  },
  {
    name: 'field-toggle',
    type: 'toggle',
    label: 'Toggle',
    hint: 'Select an option',
    value: [],
    options: [
      { label: 'yes', value: 'yes' },
      { label: 'no', value: 'no' },
      { label: 'maybe', value: 'maybe' },
      { label: 'always', value: 'always' },
      { label: 'never', value: 'never' },
    ],
    validation: Yup.array().min(2).required(),
  },
];

export const Default = () => {
  return (
    <Form
      id="story"
      type="dialog"
      align="left"
      fields={object('Fields', getFields)}
      actions={[
        {
          name: 'submit',
          label: 'Validate',
          handler: (data) => alert(data),
        },
        {
          name: 'secondary',
          label: 'Secondary action',
          handler: (data) => {
            alert('Secondary action');
            alert(data);
          },
        },
        {
          name: 'cancel',
          label: 'Reset',
          handler: () => alert('form reset'),
        },
      ]}
      defaultValues={utils.form.getInitialValues(getFields)}
      validationSchema={utils.form.getValidationSchema(getFields)}
    />
  );
};
