const styles = (theme) => ({
  hide: {
    display: 'none',
  },
  drawerPaper: {
    maxWidth: '3%',
    flexDirection: 'row',
  },
  drawerPaperExpanded: {
    width: 'auto',
    maxWidth: '20%',
    flexDirection: 'row',
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
});

export default styles;
