import React from 'react';

//app
import { TableCell, Status, Translate, Chip, Link } from 'components';
import * as utils from 'utils';
import config from 'config';

//mui
import styles from './LossActionTable.styles';
import { makeStyles, TableRow, Typography } from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

LossActionRow.defaultProps = {
  padding: 0
};

export function LossActionRow({ data, columnProps, color, padding, onClick, isOpen }) {
  const classes = makeStyles(styles, { name: 'LossActionTable' })({ expanded: isOpen });
  const hasChildren = data.actionChildItemList;

  return (
    <>
      <TableRow onClick={onClick} style={{'background': color }} className={classes.row} key={data?.actionID} data-testid={`search-row-${data?.actionID}`} hover>
        <TableCell {...columnProps('actionId')} data-testid={`row-col-${data?.actionID}`} style={{'paddingLeft': padding, 'position': 'relative' }}>
          {hasChildren &&<KeyboardArrowDownIcon className={classes.arrow}  />}
          <Link text={`${data?.actionID}`} color="secondary" onClick={() => {}} />
        </TableCell>
        <TableCell {...columnProps('name')} data-testid={`row-col-${data?.actionName}`}>
          <Chip
            label={data?.actionName}
            size="small"
            type="error"
            color='primary'
            variant="outlined"
          />
        </TableCell>
        <TableCell {...columnProps('description')} data-testid={`row-col-${data?.description}`}>
          {data?.description}
        </TableCell>
        <TableCell {...columnProps('team')} data-testid={`row-col-${data?.team}`}>
          {data?.team}
        </TableCell>
        <TableCell {...columnProps('assignedTo')} data-testid={`row-col-${data?.assignedTo}`}>
          {data?.assignedTo}
        </TableCell>
        <TableCell {...columnProps('createdDate')} data-testid={`row-col-${data?.dateCreated}`}>
          {utils.string.t('format.date', {
            value: { date: data?.dateCreated, format: config.ui.format.date.textTime },
          })}
        </TableCell>
        <TableCell {...columnProps('targetDate')} data-testid={`row-col-${data?.targetDueDate}`}>
          <Typography variant="body2" color="error">
            {utils.string.t('format.date', {
              value: { date: data?.targetDueDate, format: config.ui.format.date.text },
            })}
          </Typography>
        </TableCell>
        <TableCell {...columnProps('priority')} data-testid={`row-col-${data?.priority}`}>
          {data?.priority}
        </TableCell>
        <TableCell {...columnProps('status')} data-testid={`row-col-${data?.status}`}>
          {data?.status && <Status size="sm" text={<Translate label={data?.status} />} status={data?.status === "Completed" ? 'success' : 'error'} />}
        </TableCell>
        <TableCell {...columnProps('insured')} data-testid={`row-col-${data?.insured}`}>
          {data?.insured}
        </TableCell>
        <TableCell {...columnProps('division')} data-testid={`row-col-${data?.divisionID}`}>
          {data?.divisionID}
        </TableCell>
      </TableRow>
    </>
  )
}
