const styles = (theme) => ({
  root: {
    height: '100%',
  },
  footer: ({ isAllStepsCompleted }) => ({
    display: 'flex',
    justifyContent: `${!isAllStepsCompleted ? 'space-between' : 'flex-end'}`,
    position: 'sticky',
    bottom: 0,
    right: 0,
    left: 0,
    backgroundColor: theme.palette.neutral.lightest,
    padding: theme.spacing(1, 2),
    textAlign: 'right',
  }),
  saveButton: {
    margin: '0 5px',
    backgroundColor: "#52b3cf",
  },
  backButton: {
    margin: '0 5px',
    backgroundColor: theme.palette.neutral.lightest,
    color: theme.palette.primary.main
  },
  nextButton: {
    margin: '0 5px',
  }
});

export default styles;
