import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import isString from 'lodash/isString';

// app
import styles from './DetailsCard.styles';
import { Link, Skeleton } from 'components';
// mui
import { makeStyles, Typography } from '@material-ui/core';
import FileCopyOutlined from '@material-ui/icons/FileCopyOutlined';
DetailCardView.propTypes = {
  title: PropTypes.node,
  details: PropTypes.string,
  textAlign: PropTypes.string,
  link: PropTypes.bool,
  canCopyText: PropTypes.bool,
  handleClick: PropTypes.func,
  nestedClasses: PropTypes.shape({
    root: PropTypes.string,
    title: PropTypes.string,
    details: PropTypes.string,
  }),
};

export function DetailCardView({
  title,
  details,
  isLoading,
  textAlign,
  nestedClasses,
  link,
  handleClick,
  width,
  setHovered,
  isHovered,
  canCopyText,
  handleClipBoard,
}) {
  const showHover = isHovered && canCopyText && !isLoading;
  const classes = makeStyles(styles, { name: 'DetailCard' })({ textAlign, width, showHover });
  return (
    <div
      className={classnames(classes.root, nestedClasses.root)}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <div className={classnames(classes.subRoot)}>
        <div className={classnames(classes.detailsContainer)}>
          <Typography
            className={classnames(classes.title, nestedClasses.title)}
            variant="subtitle2"
            color="secondary"
            title={isString(title) ? title : null}
          >
            {title}
          </Typography>

          <Typography className={classnames(classes.details, nestedClasses.details)} variant="body2" title={details ? details : null}>
            {isLoading ? (
              <Skeleton height={40} animation="wave" displayNumber={1} />
            ) : link === true ? (
              <Link title={link} text={'link'} color="secondary" nestedClasses={{ link: classes.link }} handleClick={handleClick} />
            ) : (
              details
            )}
          </Typography>
        </div>
        <div>
          {details && canCopyText && !isLoading && (
            <div className={classnames(classes.clipboard)} onClick={handleClipBoard}>
              <FileCopyOutlined />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
