import React, { useState } from 'react';
import PropTypes from 'prop-types';
import styles from './LossDetail.styles';
import * as utils from 'utils';
import moment from 'moment';

//app
import { Layout, Accordion, TableHead, TableCell, Overflow } from 'components';
import { RowDetails } from 'modules';
import config from 'config';
import * as constants from 'consts';

//mui
import { makeStyles, Typography, Grid, Table, TableBody, TableRow } from '@material-ui/core';
import { Edit } from '@material-ui/icons';

LossDetailView.propTypes = {
  catCode: PropTypes.object,
  columns: PropTypes.array.isRequired,
  lossInformation: PropTypes.object.isRequired,
  claimsAssociateWithLoss: PropTypes.array.isRequired,
};

export default function LossDetailView({ catCode, columns, lossInformation, claimsAssociateWithLoss }) {
  const classes = makeStyles(styles, { name: 'LossDetail' })();
  const catCodeName = catCode?.name && catCode?.description ? `${catCode?.name} - ${catCode?.description?.substring(0, 99)}` : '';
  const [accordionsExpandStatus, setAccordiansStatus] = useState({
    [utils.string.t('loss.lossInformation.title')]: true,
    [utils.string.t('loss.relatedClaims.title')]: true,
  });
  const actions = () => {
    return [
      {
        id: '1',
        icon: Edit,
        color: 'primary',
        onClick: () => {
          alert('coming soon');
        },
      },
    ];
  };
  const accordionTitle = (title) => {
    return (
      <Typography className={classes.title} variant="body2">
        {' '}
        {title}
      </Typography>
    );
  };
  const setAccordiansIsExpanded = (cardName, isExpand) => {
    const accordionsStatus = { ...accordionsExpandStatus };
    accordionsStatus[cardName] = isExpand;
    setAccordiansStatus(accordionsStatus);
  };
  return (
    <div className={classes.wrapper}>
      <Layout main padding>
        <Accordion
          expanded={accordionsExpandStatus[utils.string.t('loss.lossInformation.title')]}
          title={accordionTitle(utils.string.t('loss.lossInformation.title'))}
          actions={actions()}
          onChange={(event, isExpanded) => setAccordiansIsExpanded(utils.string.t('loss.lossInformation.title'), isExpanded)}
        >
          <Grid container direction="row" spacing={2}>
            <Grid item xs={12} sm={6} md={3}>
              <Grid container direction="column">
                <Grid item>
                  <RowDetails title={utils.string.t('claims.lossInformation.ref')} details={lossInformation?.lossRef} textAlign="right" />
                  <RowDetails
                    title={utils.string.t('claims.lossInformation.fromDate')}
                    details={
                      (lossInformation?.fromDate && moment(lossInformation?.fromDate).format(config.ui.format.date.text)) ||
                      constants.STATUS_NOT_APPLICABLE
                    }
                    textAlign="right"
                  />
                </Grid>
              </Grid>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Grid container direction="column">
                <Grid item>
                  <RowDetails title={utils.string.t('claims.lossInformation.name')} details={lossInformation?.lossName} textAlign="right" />
                  <RowDetails
                    title={utils.string.t('claims.lossInformation.toDate')}
                    details={
                      (lossInformation?.toDate && moment(lossInformation?.toDate).format(config.ui.format.date.text)) ||
                      constants.STATUS_NOT_APPLICABLE
                    }
                    textAlign="right"
                  />
                </Grid>
              </Grid>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Grid container direction="column">
                <Grid item>
                  <RowDetails
                    title={utils.string.t('claims.lossInformation.details')}
                    details={lossInformation?.lossDescription}
                    textAlign="right"
                  />
                  <RowDetails
                    title={utils.string.t('claims.lossInformation.dateAndTime')}
                    details={
                      lossInformation?.firstContactDate && moment(lossInformation?.firstContactDate).format(config.ui.format.date.textTime)
                    }
                    textAlign="right"
                  />
                </Grid>
              </Grid>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Grid container direction="column">
                <Grid item>
                  <RowDetails
                    title={utils.string.t('claims.lossInformation.assignedTo')}
                    details={lossInformation?.assignedToName}
                    textAlign="right"
                  />
                  <RowDetails title={utils.string.t('claims.lossInformation.catCode')} details={catCodeName} textAlign="right" />
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Accordion>
        <Accordion
          expanded={accordionsExpandStatus[utils.string.t('loss.relatedClaims.title')]}
          title={accordionTitle(utils.string.t('loss.relatedClaims.title'))}
          onChange={(event, isExpanded) => setAccordiansIsExpanded(utils.string.t('loss.relatedClaims.title'), isExpanded)}
        >
          <Grid item xs={12}>
            <Grid container direction="column">
              <Overflow>
                <Table>
                  <TableHead columns={columns} />
                  <TableBody>
                    {claimsAssociateWithLoss?.map((claim, index) => (
                      <TableRow key={index}>
                        <TableCell>{claim?.claimReference || constants.STATUS_NOT_APPLICABLE}</TableCell>
                        <TableCell>{claim?.claimant || constants.STATUS_NOT_APPLICABLE}</TableCell>
                        <TableCell>{claim?.policyRef || constants.STATUS_NOT_APPLICABLE}</TableCell>
                        <TableCell>{claim?.insured || constants.STATUS_NOT_APPLICABLE}</TableCell>
                        <TableCell>{claim?.insured || constants.STATUS_NOT_APPLICABLE}</TableCell>
                        <TableCell>{claim?.insured || constants.STATUS_NOT_APPLICABLE}</TableCell>
                        <TableCell>{claim?.insured || constants.STATUS_NOT_APPLICABLE}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </Overflow>
            </Grid>
          </Grid>
        </Accordion>
      </Layout>
    </div>
  );
}
