import React from 'react';
import PropTypes from 'prop-types';

//app
import * as utils from 'utils';
import { LossesTabTableView } from './LossesTabTable.view';
import { TableFilters, TableToolbar, Button, FormGrid, FormSelect, FormText, FormContainer, FilterBar, FormAutocompleteMui } from 'components';
import styles from './LossesTab.styles';
import { useMedia } from 'hooks';
import { useForm } from 'react-hook-form';

//mui
import { makeStyles, Box } from '@material-ui/core';
import SearchIcon from '@material-ui/icons/Search';

LossesTabView.propTypes = {
  claims: PropTypes.object.isRequired,
  sort: PropTypes.object.isRequired,
  columnsArray: PropTypes.array.isRequired,
  columnProps: PropTypes.func.isRequired,
  isTableHidden: PropTypes.bool.isRequired,
  handleSearch: PropTypes.func.isRequired,
  handleSort: PropTypes.func.isRequired,
  handleChangePage: PropTypes.func.isRequired,
  handleChangeRowsPerPage: PropTypes.func.isRequired,
  handleSearchFilter: PropTypes.func.isRequired,
  handleCreateClaim: PropTypes.func.isRequired,
  tableFilterFields: PropTypes.array.isRequired,
  onResetFilter: PropTypes.func.isRequired,
  toggleColumn: PropTypes.func.isRequired,
};

export function LossesTabView({
  lossesData,
  tableData,
  sort,
  columnsArray,
  columnProps,
  isTableHidden,
  handleSearch,
  handleSort,
  handleChangePage,
  handleChangeRowsPerPage,
  handleSearchFilter,
  handleCreateClaim,
  tableFilterFields,
  onResetFilter,
  toggleColumn,
  searchField,
  searchTerm,
  isTblLoader,
  emptyData,
}) {
  const media = useMedia();
  const classes = makeStyles(styles, { name: 'LossesTab' })({ isMobile: media.mobile });
  const defaultValues = utils.form.getInitialValues(searchField);
  const { control } = useForm({ defaultValues });

  return (
    <Box mt={3} data-testid="losses-tab">
      <Box mt={3}>
        
        <TableToolbar>
          <TableFilters
            search
            searchBy={
              <Box pr={1}>
                  <FormAutocompleteMui
                  {...utils.form.getFieldProps(searchField, 'searchBy')}
                  control={control}
                  nestedClasses={{ root: classes.searchBySelect }}
                />
              </Box>
            }
            className={classes.filterToggleBtn}
            searchMinChars={4}
            searchPlaceholder={utils.string.t('claims.columns.claimsList.searchLosses')}
            searchTerm={searchTerm || ''}
            nestedClasses={{ searchMaxWidth: classes.searchMaxWidth, searchLeft: classes.searchLeft }}
            filtersArray={tableFilterFields}
            columns
            handlers={{
              onSearch: handleSearch,
              onResetFilter: onResetFilter,
              onFilter: handleSearchFilter,
              onToggleColumn: toggleColumn,
            }}
          />
        </TableToolbar>
      </Box>
      {!isTableHidden && (
        <LossesTabTableView
          tableData={tableData || []}
          cols={columnsArray}
          columnProps={columnProps}
          sort={sort}
          pagination={{
            page: lossesData.page,
            rowsTotal: lossesData.itemsTotal,
            rowsPerPage: lossesData.pageSize,
          }}
          handleSort={handleSort}
          handleChangePage={handleChangePage}
          handleChangeRowsPerPage={handleChangeRowsPerPage}
          handleCreateClaim={handleCreateClaim}
          isTblLoader={isTblLoader}
          emptyData={emptyData}
        />
      )}
    </Box>
  );
}
