const styles = (theme) => ({
  filterToggleBtn: {
    margin: theme.spacing(0.25),
  },
  searchBySelect: {
    '& .MuiInputBase-root': {
      height: '32px',
      alignContent: 'center',
      marginRight: '50px',
    },
  },
  searchMaxWidth: {
    minWidth: '45% !important',
    width: '50% !important',
  },
  searchLeft: {
    marginLeft: '3px !important',
  },
  row: {
    cursor: 'pointer',
    '&.active': {
      backgroundColor: theme.palette.grey[300],
    },
  },
  rowSelected: {
    background: theme.palette.grey[100],
  },
  colSelected: {
    background: theme.palette.grey[300],
  },
  colBorder: {
    borderLeft: `4px solid ${theme.palette.grey[600]}`,
  },
  wrapper: {
    marginTop: 30,
  },
  catCodeDescription: {
    fontSize: theme.typography.pxToRem(12),
    width: '150px',
    overflow: 'hidden',
    whiteSpace: 'nowrap',
    textOverflow: 'ellipsis',
  },
  inFlightText: {
    marginTop: '16px !important',
  },
  inFlightMarks: {
    borderRadius: 4,
    border: '1px solid',
    marginBottom: 14,
    height: 13,
    width: 14,
  },
});

export default styles;
