import React,{useEffect, useState} from 'react';
import PropTypes from 'prop-types';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import { useDispatch } from 'react-redux';

// app
import {postLossInformation, postEditLossInformation} from 'stores';
import styles from './ClaimsEnterLossInformation.styles';
import {
  FormAutocompleteMui,
  FormContainer,
  FormLegend,
  FormFields,
  FormGrid,
  FormText,
  FormDate,
  FormLabel,
  FormHidden,
  Tooltip
} from 'components';
import { ClaimsUploadViewSearchDocs, RegisterNewLossFixedBottomBar} from 'modules';
import * as utils from 'utils';
import * as constants from 'consts';

// mui
import { makeStyles, Box, Grid } from '@material-ui/core';

ClaimsEnterLossInformationView.propTypes = {
  fields: PropTypes.array.isRequired,
  isAllStepsCompleted: PropTypes.bool.isRequired,
  activeStep: PropTypes.number.isRequired,
  lastStep: PropTypes.bool.isRequired,
  handleCancel: PropTypes.func.isRequired,
  handleFinish: PropTypes.func.isRequired,
  handleNext: PropTypes.func.isRequired,
  handleBack: PropTypes.func.isRequired,
  handleSave: PropTypes.func.isRequired,
  nestedClasses: PropTypes.shape({
    root: PropTypes.string,
  }),
  hasLossRef: PropTypes.bool.isRequired,
  lossInformation: PropTypes.object.isRequired,
  lossDocsList: PropTypes.array
};

export function ClaimsEnterLossInformationView(props) {
  const { fields, handleSave, handleNext, handleCancel, hasLossRef, lossInformation, loggedInUser, handleBack, lossDocsList, isWarningShow, validation, setValidation } = props;
  const dispatch = useDispatch();
  const classes = makeStyles(styles, { name: 'ClaimsEnterLossInformation' })();
  const defaultValues = utils.form.getInitialValues(fields);
  const validationSchema = utils.form.getValidationSchema(fields);

  const { handleSubmit, errors, control, watch } = useForm({
    defaultValues,
    ...(validationSchema && { resolver: yupResolver(validationSchema), context: { validation } }),
  });
  const [isFormsEdited, setIsFormsEdited] = useState(false);
  
  const lossNameEdit = watch('lossName');
  const lossDescriptionEdit = watch('lossDescription');
  const fromDateEdit = watch('fromDate');
  const toDateEdit = watch('toDate');
  const catCodesIDEdit = watch('catCodesID');
  
  useEffect(()=>{
    
    if(lossInformation?.lossName !== lossNameEdit || lossInformation?.lossDescription !== lossDescriptionEdit 
      || lossInformation?.fromDate?.toString() !== fromDateEdit?.toString()
      || lossInformation?.toDate?.toString() !== toDateEdit?.toString() 
      || lossInformation?.catCodesID?.toString() !== catCodesIDEdit.id
      ){
      setIsFormsEdited(true);
    }
    if(props.saveStatus && isFormsEdited){
        handleSubmit(submitLossInformation)()
    }
    
  },[props.saveStatus,isFormsEdited]) // eslint-disable-line react-hooks/exhaustive-deps


  useEffect(() => {
    if (validation) {
      handleSubmit(handleNext)();
    }
    setValidation(false);
  }, [validation]); // eslint-disable-line react-hooks/exhaustive-deps

  const submitLossInformation = async (values) => {
    if (lossInformation?.lossDetailID) {
      await dispatch(postEditLossInformation(values));
      setIsFormsEdited(false)
    } else {
      await dispatch(postLossInformation(values));
      setIsFormsEdited(false)
      handleSubmit(handleNext)
    }
  }
  
  
  return (
    <>
      <Box mt={4}>
        <Grid container direction="row" justify="space-between" alignItems="center">
          <Grid item>
            <FormLegend text={utils.string.t('claims.lossInformation.title')} nestedClasses={{ root: classes.lossInfoTitle }} />
          </Grid>
        </Grid>
      </Box>
      <FormContainer data-testid="form-lossInformation">
        <FormFields type="dialog">
          <FormGrid container spacing={6}>
            <FormGrid item xs={12} sm={6}>
              <FormGrid container>
                <FormGrid item xs={6}>
                  <FormText {...utils.form.getFieldProps(fields, 'lossRef')} />
                </FormGrid>
                <FormGrid item xs={6}>
                  <FormDate {...utils.form.getFieldProps(fields, 'fromDate', control)} error={errors.fromDate} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormText {...utils.form.getFieldProps(fields, 'lossName', control)} error={errors.lossName} />
                  {isWarningShow && <span className={classes.warningMessage}>{utils.string.t('claims.lossInformation.validation.lossNameWarning')}</span>}
               </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormText {...utils.form.getFieldProps(fields, 'lossDescription', control)} error={errors.lossDescription} />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
                <FormGrid item xs={12}>
                  <FormAutocompleteMui
                    {...utils.form.getFieldProps(fields, 'catCodesID', control)}
                    nestedClasses={{ root: classes.catCodeSelect }}
                  />
                </FormGrid>
              </FormGrid>
            </FormGrid>

            <FormGrid item xs={12} sm={6}>
              <FormGrid container>
                <FormGrid item xs={8}>
                  <FormDate {...utils.form.getFieldProps(fields, 'toDate', control)} error={errors.fromDate} />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
              <FormGrid item xs={8}>
                  <Tooltip title={utils.string.t('claims.lossInformation.firstContactTooltipMessage')} block placement="bottom">
                  <FormGrid container alignItems="center" spacing={2}>
                    <FormGrid item xs={7}>
                      <FormDate {...utils.form.getFieldProps(fields, 'firstContactDate', control)} error={errors.firstContactDate} />
                    </FormGrid>
                    <FormGrid item xs={5} nestedClasses={{ root: classes.lossTime }}>
                      <FormText {...utils.form.getFieldProps(fields, 'firstContactTime', control)} />
                    </FormGrid>
                  </FormGrid>
                  </Tooltip>
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={6}>
                  <FormLabel label={utils.string.t('claims.lossInformation.assignedTo')} align="left" />
                  <FormLabel label={loggedInUser.fullName} align="left" />
                </FormGrid>
              <FormHidden {...utils.form.getFieldProps(fields, 'assignedTo', control)} />
              </FormGrid>
            </FormGrid>
          </FormGrid>
        </FormFields>
      </FormContainer>
      <Box className={classes.uploadDocumentContainer}>
        <Grid container>
          <Grid item xs={12}>
            <ClaimsUploadViewSearchDocs
                refData={lossInformation}
                refIdName={constants.DMS_CONTEXT_LOSS_ID}
                dmsContext={constants.DMS_CONTEXT_LOSS}
                documentTypeKey={constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.type.claims}
                isTabView={false}
                isClaims
                isClaimsUploadDisabled={!hasLossRef}
                claimsUploadWarningMsg={!hasLossRef ? utils.string.t('claims.lossInformation.dms.fileUploadWarning') : ''}
                claimsSearchDocumentsTxt={utils.string.t('claims.lossInformation.dms.searchDocuments')}
                uploadDocumentsTitle={utils.string.t('claims.lossInformation.dms.uploadDocuments')}
                docList={lossDocsList}
              />
          </Grid>
        </Grid>
      </Box>

      <RegisterNewLossFixedBottomBar
          {...props}
         handleSave={handleSubmit(handleSave)}
         handleNextSubmit={() => setValidation(true)}
         save={true}
         handleBack={() => { handleBack(0) }}
         handleCancel={handleCancel}
      />
    </>
  );
}
