import { useEffect, useState, useRef } from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { useSelector, useDispatch } from 'react-redux';

// app
import {
  hideModal,
  showModal,
  removeFileUpload,
  getUploadedFiles,
  fetchDocumentMetaData,
  getDocumentMetaData,
  uploadFiles,
  getDmsContext,
  postDmsDocuments,
  getDocumentUploaded,
  selectRefDataNewDocumentTypesByContextSource,
  setDmsContext,
  selectSettlementCurrency,
  resetFileUpload,
  selectRefDataXbInstances,
  resetPostStatus,
  updateUploadDocumentDto,
  updateFileName,
  selectorDmsViewFiles,
  selectLossId,
} from 'stores';
import * as utils from 'utils';
import * as constants from 'consts';

const useDmsUpload = (documentTypeKey, selectedSourceId, metaDataSectionRef, postDmsDocumentsSuccess) => {
  const dispatch = useDispatch();
  const errorRef = useRef();

  // form
  const { control, formState, handleSubmit, register, setValue, setError, getValues, clearErrors } = useForm();

  // state
  const [selectedItem, setSelectedItem] = useState('');
  const [rowIndex, setRowIndex] = useState(null);
  const [indexDocTypeUnfilled, setRowIndexDocType] = useState([]);
  const [postSubmissionRowIndex, setPostSubmissionRowIndex] = useState(null);
  const [selectedItemClassification, setSelectedItemClassification] = useState('');
  const [rowIndexClassification, setRowIndexClassification] = useState(`filesClassification${'3'}`);
  const [isMetaDataLoaded, setIsMetaDataLoaded] = useState(false);

  // redux
  const uploadedFiles = useSelector(getUploadedFiles);
  const { documentMetaData } = useSelector(getDocumentMetaData);
  const dmsContext = useSelector(getDmsContext);
  const refDataXbInstances = useSelector(selectRefDataXbInstances);
  const { isFulfilled, isPending, isRejected, documentDto, refDocumentDto } = useSelector(getDocumentUploaded);
  const documentDetails = useSelector(selectorDmsViewFiles);
  const lossInformation = useSelector(selectLossId);

  const xbInstanceId = selectedSourceId ? selectedSourceId : constants.DMS_CLAIM_SOURCE_ID;

  const { dmsSectionKey, sourceId } = utils.dmsFormatter.getDocumentTypeFilterKeys(dmsContext, xbInstanceId, documentTypeKey);
  const isDmsFromPi = utils.dmsFormatter.isDmsFromPi(documentTypeKey);
  const { documentTypeDescription, sectionKey, dmsSourceID } = isDmsFromPi && utils.dmsFormatter.getDocumentTypeInfo(documentTypeKey, sourceId);
  const documentTypesBeforeFilter = useSelector(selectRefDataNewDocumentTypesByContextSource(dmsSectionKey, sourceId));
  const documentTypesAfterFilter =
    (dmsContext === constants.DMS_CONTEXT_POLICY || dmsContext === constants.DMS_CONTEXT_PROCESSING_INSTRUCTION) && isDmsFromPi
      ? documentTypesBeforeFilter?.filter(
          (type) =>
            type.documentTypeDescription === documentTypeDescription && type.sectionKey === sectionKey && type.sourceID === dmsSourceID
        )
      : documentTypesBeforeFilter;

  let currencies = useSelector(selectSettlementCurrency);
  currencies = currencies.map((currency) => ({ ...currency, name: `${currency.id} - ${currency.name}` }));

  const docClassificationTypes = [
    { id: 1, value: utils.string.t('dms.upload.modalItems.classificationType.low') },
    { id: 2, value: utils.string.t('dms.upload.modalItems.classificationType.guarded') },
    { id: 3, value: utils.string.t('dms.upload.modalItems.classificationType.high') },
    { id: 4, value: utils.string.t('dms.upload.modalItems.classificationType.severe') },
  ];
  const { commonInfo, lossInfo, claimInfo, policyInfo } = utils.dmsFormatter.formatDocumentMetaData(
    documentMetaData,
    dmsContext,
    refDataXbInstances,
    lossInformation?.lossRef
  );

  const cols = [
    {
      id: 'fileName',
      label: utils.string.t('claims.lossInformation.dmsUploadDocument.cols.fileName'),
    },
    {
      id: 'documentType',
      label: utils.string.t('claims.lossInformation.dmsUploadDocument.cols.documentType'),
    },
    {
      id: 'documentClassification',
      label: utils.string.t('claims.lossInformation.dmsUploadDocument.cols.documentClassification'),
    },
    {
      id: 'tag',
      label: utils.string.t('claims.lossInformation.dmsUploadDocument.cols.tag'),
    },
    {
      id: 'removeDoc',
      empty: true,
    },
  ];

  const {
    fields: fieldsArray,
    append: appendFieldArray,
    remove: removeFieldArray,
  } = useFieldArray({
    control,
    name: 'files',
  });

  const getFileKey = utils.dmsFormatter.getFileKey;
  const duplicateIndex = utils.dmsFormatter.getDuplicateIndexes(fieldsArray, documentDetails);

  const handleCancel = () => () => {
    dispatch(hideModal(constants.DMS_MODULE));
  };

  const handleShowModal = (newContext) => (files) => {
    dispatch(setDmsContext(newContext));
    dispatch(
      showModal({
        component: 'DMS_UPLOAD',
        props: {
          fullWidth: true,
          title: utils.string.t('dms.upload.modalItems.uploadDocuments'),
          maxWidth: 'xl',
          componentProps: {
            confirmLabel: utils.string.t('app.ok'),
            cancelLabel: utils.string.t('app.goBack'),
            confirmMessage: utils.string.t('processingInstructions.documentsWillNotBeSaved'),
            buttonColors: { confirm: 'secondary', cancel: 'primary' },
          },
        },
      })
    );
    const uniqueFiles = utils.dmsFormatter.getUniqueFiles(files, uploadedFiles);
    dispatch(uploadFiles(uniqueFiles));
  };

  const handleSubmitUpload = (formValues) => {
    utils.dmsFormatter.getInvalidInputs(fieldsArray, formValues, setError);
    const isDocTypeVaild = utils.dmsFormatter.getSelectedDocumentType(formValues, uploadedFiles).length > 0;
    setRowIndexDocType(utils.dmsFormatter.getSelectedDocumentType(formValues, uploadedFiles));

    if (!isDocTypeVaild || (isFulfilled && formState.isSubmitSuccessful)) {
      const metaData = {
        catCodesID: documentMetaData?.catCodesID,
        year: documentMetaData?.year,
        lossName: documentMetaData?.lossName,
        documentTypeId: selectedItem?.documentTypeID,
        sourceId: documentMetaData?.xbInstanceId,
        documentTypeDescription: selectedItem?.documentTypeDescription,
        claimId: documentMetaData?.claimId,
      };

      if (!errorRef.current) {
        dispatch(postDmsDocuments({ fieldsArray, metaData, formValues, documentTypeKey })).then((data) => {
          if (data?.status?.toLowerCase() === constants.API_RESPONSE_OK.toLowerCase()) postDmsDocumentsSuccess(data);
        });
      }
    }
  };

  const isActionDispatched = utils.dmsFormatter.getActionDispatched(isPending, isFulfilled, isRejected);
  const hasPaymentDocumentType = utils.dmsFormatter.getPaymentDocumentType(selectedItem);
  const getIcons = utils.dmsFormatter.getIcons;

  const handleRemoveFiles = (fileName) => (index) => {
    if (isActionDispatched || !formState.isSubmitSuccessful) {
      removeFieldArray(index);
      dispatch(removeFileUpload(fileName));
    }
  };

  const handleSelectDocClassification = (selectedItemClassification, index) => {
    setSelectedItemClassification(selectedItemClassification);
    setValue(`filesClassification${index}`, selectedItemClassification);
    setRowIndexClassification(index);
  };
  const handleResetFilter = () => {
    fieldsArray?.forEach((data, index) => {
      setValue(`files${index}`, undefined);
    });
    setSelectedItem('');
  };
  const handleSelectedItem = (selectedItem, index) => {
    setSelectedItem(selectedItem);
    setValue(`files${index}`, selectedItem);
    setRowIndex(index);
    if (indexDocTypeUnfilled.includes(index)) {
      const arr = indexDocTypeUnfilled;
      setRowIndexDocType(arr.filter((data) => data !== index));
    }
  };

  const handleInputChange = (name, index) => (e) => {
    setValue(name, e.target.value);
    dispatch(updateFileName({ fileName: e.target.value, fileIndex: index }));

    if (errorRef.current) {
      clearErrors(name);
    }
  };

  const handleSingleFileSubmission = (index) => {
    setPostSubmissionRowIndex(index);

    return utils.dmsFormatter.setCurrentFieldValues(index, getValues);
  };

  useEffect(() => {
    if (isFulfilled) {
      documentDto.forEach((item, i) => {
        if (item.documentUploaded) {
          dispatch(removeFileUpload(item.documentName));
          dispatch(updateUploadDocumentDto(item.documentName));
        }
      });
    }
  }, [isFulfilled, documentDto, dispatch]);

  useEffect(() => {
    dispatch(fetchDocumentMetaData(xbInstanceId, metaDataSectionRef)).then((data) => {
      setIsMetaDataLoaded(data?.status === constants.API_RESPONSE_OK);
    });
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    removeFieldArray(fieldsArray);
    appendFieldArray(uploadedFiles);
    // TODO - remove this rule in the next PR
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [uploadedFiles?.length]);

  useEffect(() => {
    if (Object.keys(formState.errors).length > 0) {
      errorRef.current = true;
    } else {
      errorRef.current = false;
    }
  }, [formState.errors]);

  useEffect(() => {
    return () => {
      if (!isActionDispatched) {
        dispatch(resetFileUpload());
      }
    };
  }, [isActionDispatched, dispatch]);

  return {
    uploadedFiles,
    getFileKey,
    handleRemoveFiles,
    fieldsArray,
    formState,
    control,
    cols,
    handleShowModal,
    commonInfo,
    lossInfo,
    claimInfo,
    policyInfo,
    handleSubmit,
    handleSubmitUpload,
    isPending,
    isRejected,
    isFulfilled,
    getIcons,
    dmsContext,
    handleCancel,
    documentTypes: documentTypesAfterFilter,
    hasPaymentDocumentType,
    rowIndex,
    handleSelectedItem,
    selectedItem,
    documentDto,
    register,
    getValues,
    duplicateIndex,
    handleSelectDocClassification,
    selectedItemClassification,
    rowIndexClassification,
    docClassificationTypes,
    indexDocTypeUnfilled,
    setValue,
    currencies,
    handleInputChange,
    resetPostStatus,
    handleSingleFileSubmission,
    isActionDispatched,
    handleResetFilter,
    postSubmissionRowIndex,
    refDocumentDto,
    isDmsFromPi,
    isMetaDataLoaded,
  };
};

export default useDmsUpload;
