const styles = (theme) => ({
    wrapper: {
        cursor: 'pointer'
    },
    title: {
        marginBottom: theme.spacing(0.25),
    },
    panel: {
        boxShadow: 'none',
    },
    openInNewOutIcon: {
        position: 'absolute',
        right: 0,
        top: 0,
        bottom: 0,
        margin: 'auto'
    },
    arrow: {
        flex: '0 0 auto',
        marginRight: theme.spacing(1.5),
        verticalAlign: 'middle',
        transition: theme.transitions.create('transform'),
    },
});

export default styles;
