import React, { useEffect } from 'react';
import LossDetailView from './LossDetail.view';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';

// app
import * as utils from 'utils';
import {
  getCatCodes,
  getLossInformation,
  getClaimsAssociateWithLoss,
  selectClaimAssociateWithLoss,
  selectCatCodes,
  selectLossId,
  selectLossInfoIsLoading,
  selectClaimAssociateWithLossLoading
} from 'stores';
import config from 'config';
import * as constants from 'consts';

export default function LossDetail() {
  const dispatch = useDispatch();
  const claimsAssociateWithLoss = useSelector(selectClaimAssociateWithLoss);
  const catCodes = useSelector(selectCatCodes);
  const lossInformation = useSelector(selectLossId);
  const isLossInfoLoading = useSelector(selectLossInfoIsLoading);
  const isClaimAssociateWithLossLoading = useSelector(selectClaimAssociateWithLossLoading);
  const catCode = catCodes.find((item) => Number(item.id) === lossInformation.catCodesID);
  const catCodeName = catCode?.name && catCode?.description ? `${catCode?.name} - ${catCode?.description?.substring(0, 99)}` : '';
 
  useEffect(() => {
    dispatch(getCatCodes(false));
    dispatch(getLossInformation({ lossDetailsId: 8832, viewLoader: false }));
    dispatch(getClaimsAssociateWithLoss(7237,false));
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const relatedClaimColumns = [
    {
      id: 'claimReference',
      label: utils.string.t('loss.relatedClaims.columns.claimReference'),
    },
    {
      id: 'claimant',
      label: utils.string.t('loss.relatedClaims.columns.claimant'),
    },
    {
      id: 'policyRef',
      label: utils.string.t('loss.relatedClaims.columns.policyReference'),
    },
    {
      id: 'claimReceived',
      label: utils.string.t('loss.relatedClaims.columns.claimReceived'),
    },
    {
      id: 'division',
      label: utils.string.t('loss.relatedClaims.columns.division'),
    },
    {
      id: 'insured',
      label: utils.string.t('loss.relatedClaims.columns.insured'),
    },
    {
      id: 'riskDetails',
      label: utils.string.t('loss.relatedClaims.columns.riskDetails'),
    },
    {
      id: 'claimStatus',
      label: utils.string.t('loss.relatedClaims.columns.claimStatus'),
    },
  ];

  const lossInfo = [
    {
      title: utils.string.t('claims.lossInformation.ref'),
      value: lossInformation?.lossRef,
      isLoading: isLossInfoLoading,
    },
    {
      title: utils.string.t('claims.lossInformation.name'),
      value: lossInformation?.lossName,
      isLoading: isLossInfoLoading,
    },
    {
      title: utils.string.t('claims.lossInformation.details'),
      value: lossInformation?.lossDescription,
      isLoading: isLossInfoLoading,
    },
    { title: utils.string.t('claims.lossInformation.assignedTo'), 
      value: lossInformation?.assignedToName, 
      isLoading: isLossInfoLoading 
    },
    {
      title: utils.string.t('claims.lossInformation.fromDate'),
      value: (lossInformation?.fromDate && moment(lossInformation?.fromDate).format(config.ui.format.date.text)) ||
      constants.STATUS_NOT_APPLICABLE,
      isLoading: isLossInfoLoading,
    },
    { title: utils.string.t('claims.lossInformation.toDate'),
     value: (lossInformation?.toDate && moment(lossInformation?.toDate).format(config.ui.format.date.text)) ||
     constants.STATUS_NOT_APPLICABLE, 
     isLoading: isLossInfoLoading 
    },
    {
      title: utils.string.t('claims.lossInformation.dateAndTime'),
      value: lossInformation?.firstContactDate && moment(lossInformation?.firstContactDate).format(config.ui.format.date.textTime),
      isLoading: isLossInfoLoading,
    },
    { title: utils.string.t('claims.lossInformation.catCode'), 
      value: catCodeName, 
      isLoading: isLossInfoLoading 
    },
    
  ];

  return (
    <LossDetailView
      columns={relatedClaimColumns}
      lossInfo={lossInfo}
      catCode={catCode}
      claimsAssociateWithLoss={claimsAssociateWithLoss}
      isClaimAssociateWithLossLoading={isClaimAssociateWithLossLoading}
    />
  );
}
