import { React, useEffect, useState } from "react";
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';

//app
import { Translate } from 'components';
import { selectDmsWidgetExpanded } from 'stores';
import styles from './CustomizedDialog.styles';

//mui
import NoteAddIcon from '@material-ui/icons/NoteAdd';
import { makeStyles } from '@material-ui/core';
import { withStyles } from "@material-ui/core/styles";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";

CustomizedDialog.propTypes = {
  drawerOpen: PropTypes.bool.isRequired,
  dialogOpen: PropTypes.bool.isRequired,
  modalTitle: PropTypes.string.isRequired,
  handlers: PropTypes.shape({
    handleDialogClose: PropTypes.func.isRequired,
  }),
  isDrawerExpanded: PropTypes.bool.isRequired
}

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  const nestedClasses = makeStyles(styles, { name: 'CustomizedDialog' })();
  return (
    <MuiDialogTitle disableTypography className={nestedClasses.root} {...other}>
      <Typography>{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={nestedClasses.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2)
  }
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1)
  }
}))(MuiDialogActions);


function CustomizedDialog({
  parentRef,
  children,
  dialogOpen,
  modalTitle,
  handlers,
}) {
  const nestedClasses = makeStyles(styles, { name: 'CustomizedDialog' })();
  const [openDialog, setOpenDialog] = useState(false);
  const isDmsWidgetExpanded = useSelector(selectDmsWidgetExpanded);

  const handleClose = () => {
    setOpenDialog(false);
    handlers.handleDialogClose();
  };

  useEffect(() => {
    setOpenDialog(dialogOpen)
  }, [dialogOpen]);

  return (
    <div>
      <Dialog
        maxWidth="xl"
        className={isDmsWidgetExpanded ? nestedClasses.rootMargin : {}}
        fullWidth={true}
        onClose={handleClose}
        disableBackdropClick={true}
        aria-labelledby="customized-dialog-title"
        open={openDialog}
        disablePortal
        container={() => parentRef.current}
      >
        <DialogTitle onClose={handleClose}>
          <div className={nestedClasses.modalTitle}>
            <div className={nestedClasses.icon}>
              <NoteAddIcon fontSize="inherit" data-testid={'claims-register-new-loss-header'} />
            </div>
            <Translate label={modalTitle} variant="h3" />
          </div>
        </DialogTitle>
        <DialogContent dividers>
          <div>{children}</div>
        </DialogContent>
        <DialogActions>

        </DialogActions>
      </Dialog>
    </div>
  );
}

export default withStyles(styles)(CustomizedDialog);
