import React from 'react';
import { useForm } from 'react-hook-form';

import { FormFields } from 'components';
import { select, withKnobs, text } from '@storybook/addon-knobs';
import { Box } from '@material-ui/core';
import { FormText } from 'components';

export default {
  title: 'FormFields',
  component: FormFields,
  decorators: [withKnobs],
};

export const Default = () => {
  const { control, errors } = useForm({
    defaultValues: [],
  });
  const options = ['default', 'dialog', 'blank'];
  const defaultValue = 'default';
  const type = select('Type', options, defaultValue);
  const fields = [
    {
      group: 'ADDRESS',
      indicative: false,
      label: 'Street address',
      name: 'street',
      type: 'TEXT',
      placeholder: 'Type Street address',
      validation: {},
      gridSize: {
        xs: 5,
        sm: 4,
        md: 4,
        lg: 4,
        xl: 4,
      },
    },
    {
      group: 'ADDRESS',
      indicative: false,
      label: 'City',
      name: 'city',
      type: 'TEXT',
      validation: {},
      hint: 'City goes here',
      gridSize: {
        xs: 4,
        sm: 4,
        md: 4,
        lg: 4,
        xl: 4,
      },
    },
  ];

  const renderFields = fields.map((field, index) => {
    const { gridSize, ...props } = field;
    return (
      <FormText
        {...props}
        control={control}
        key={`form_${index}_${field.name}`}
        type={text('Type', field.type)}
        name={field.name}
        label={text('Label', field.label)}
        placeholder={text('Placeholder', field.placeholder)}
        hint={text('Hint', field.hint)}
        error={text('Error', errors[field.name])}
        muiComponentProps={field.muiComponentProps}
      />
    );
  });

  return (
    <Box>
      <FormFields type={type}>{renderFields}</FormFields>
    </Box>
  );
};
