import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';
// app
import { DetailCardView } from './DetailsCard.view';
import * as utils from 'utils';
import { enqueueNotification } from 'stores';


DetailCard.propTypes = {
  title: PropTypes.node,
  details: PropTypes.string,
  textAlign: PropTypes.string,
  nestedClasses: PropTypes.shape({
    root: PropTypes.string,
    title: PropTypes.string,
    details: PropTypes.string,
  }),
  handleClick: PropTypes.func,
  'data-testid': PropTypes.string,
  link: PropTypes.bool,
  isLoading: PropTypes.bool,
};

DetailCard.defaultProps = {
  textAlign: 'left',
  width: '100%',
  nestedClasses: {},
  handleClick: () => { },
  isLoading: false,
  canCopyText: true
};


export function DetailCard({ title, details, textAlign, nestedClasses, link, handleClick, width, isLoading, canCopyText }) {
  const [isHovered, setHovered] = useState(false)
  const dispatch = useDispatch();

  const handleClipBoard = () => {
    if (navigator.clipboard !== undefined) {
      //Chrome and other 
      navigator.clipboard.writeText(details).then(
        function () {
          dispatch(enqueueNotification(`Copied to clipboard successfully`, 'success'));
        },
        function (err) {
          dispatch(enqueueNotification(err, 'error'));
        }
      );
    } else if (window.clipboardData) {
      // Internet Explorer
      window.clipboardData.setData('Text', details);
      dispatch(enqueueNotification(`Copied to clipboard successfully`, 'success'));
    }

  }    // this function will be replaced in soon when POC is complete



  return (
    <DetailCardView
      title={title}
      details={details}
      textAlign={textAlign}
      nestedClasses={nestedClasses}
      link={link}
      handleClick={handleClick}
      width={width}
      isLoading={isLoading}
      setHovered={setHovered}
      isHovered={isHovered}
      canCopyText={canCopyText}
      handleClipBoard={handleClipBoard}
    />
  );
}

export default DetailCard;
