import React from 'react';
import PropTypes from 'prop-types';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';

// app
import styles from './ClaimsEnterLossInformation.styles';
import {
  FormAutocompleteMui,
  FormContainer,
  FormLegend,
  FormFields,
  FormGrid,
  FormText,
  FormDate,
  FormLabel,
  FormRequired,
} from 'components';
import { ClaimsFixedBottomBar, ClaimsUploadViewSearchDocs } from 'modules';
import * as utils from 'utils';
import * as constants from 'consts';

// mui
import { makeStyles, Box, Grid } from '@material-ui/core';

ClaimsEnterLossInformationView.propTypes = {
  fields: PropTypes.array.isRequired,
  isAllStepsCompleted: PropTypes.bool.isRequired,
  activeStep: PropTypes.number.isRequired,
  lastStep: PropTypes.bool.isRequired,
  handleCancel: PropTypes.func.isRequired,
  handleFinish: PropTypes.func.isRequired,
  handleNext: PropTypes.func.isRequired,
  handleBack: PropTypes.func.isRequired,
  handleSave: PropTypes.func.isRequired,
  nestedClasses: PropTypes.shape({
    root: PropTypes.string,
  }),
  hasLossRef: PropTypes.bool.isRequired,
  lossInformation: PropTypes.object.isRequired,
};

export function ClaimsEnterLossInformationView(props) {
  const { fields, handleSave, handleNext, handleCancel, hasLossRef, lossInformation } = props;
  const classes = makeStyles(styles, { name: 'ClaimsEnterLossInformation' })();

  const defaultValues = utils.form.getInitialValues(fields);
  const validationSchema = utils.form.getValidationSchema(fields);

  const { handleSubmit, errors, control, watch } = useForm({
    defaultValues,
    ...(validationSchema && { resolver: yupResolver(validationSchema) }),
  });

  const isFormEdited = Boolean(watch('lossName'));

  const catCodesID = watch('catCodesID');
  const isDatesRequired = catCodesID !== null && catCodesID?.id !== '0';
  return (
    <>
      <Box mt={4}>
        <Grid container direction="row" justify="space-between" alignItems="center">
          <Grid item>
            <FormLegend text={utils.string.t('claims.lossInformation.title')} nestedClasses={{ root: classes.lossInfoTitle }} />
          </Grid>
        </Grid>
      </Box>
      <FormContainer data-testid="form-lossInformation">
        <Box mt={-6} mb={3}>
          <FormRequired />
        </Box>
        <FormFields>
          <FormGrid container spacing={6}>
            <FormGrid item xs={12} sm={5}>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={utils.string.t('claims.lossInformation.ref')} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormText {...utils.form.getFieldProps(fields, 'lossRef')} />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.lossInformation.fromDate')} ${isDatesRequired ? '*' : ''}`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormDate {...utils.form.getFieldProps(fields, 'fromDate', control)} error={errors.fromDate} />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.lossInformation.toDate')} ${isDatesRequired ? '*' : ''}`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormDate {...utils.form.getFieldProps(fields, 'toDate', control)} error={errors.toDate} />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={utils.string.t('claims.lossInformation.dateAndTime')} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormGrid container alignItems="center" spacing={2}>
                    <FormGrid item xs={7}>
                      <FormDate {...utils.form.getFieldProps(fields, 'firstContactDate', control)} error={errors.firstContactDate} />
                    </FormGrid>
                    <FormGrid item xs={5}>
                      <FormText {...utils.form.getFieldProps(fields, 'firstContactTime', control)} />
                    </FormGrid>
                  </FormGrid>
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid item xs={12} sm={7}>
              <FormGrid container>
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.lossInformation.name')}*`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormText {...utils.form.getFieldProps(fields, 'lossName', control)} error={errors.lossName} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.lossInformation.details')}*`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormText {...utils.form.getFieldProps(fields, 'lossDescription', control)} error={errors.lossDescription} />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={utils.string.t('claims.lossInformation.catCode')} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormAutocompleteMui
                    {...utils.form.getFieldProps(fields, 'catCodesID', control)}
                    nestedClasses={{ root: classes.catCodeSelect }}
                  />
                </FormGrid>
              </FormGrid>
            </FormGrid>
          </FormGrid>
        </FormFields>
      </FormContainer>
      <Grid container>
        <Grid item xs={12}>
           <ClaimsUploadViewSearchDocs
              refData={lossInformation}
              refIdName={constants.DMS_CONTEXT_LOSS_ID}
              dmsContext={constants.DMS_CONTEXT_LOSS}
              documentTypeKey={constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.type.claims}
              isTabView={false}
              isClaims
              isClaimsUploadDisabled={!hasLossRef}
              claimsUploadWarningMsg={!hasLossRef ? utils.string.t('claims.lossInformation.fileUploadWarning') : ''}
              claimsSearchDocumentsTxt={utils.string.t('claims.lossInformation.searchDocuments')}
              uploadDocumentsTitle={utils.string.t('claims.lossInformation.uploadDocuments')}
            />
        </Grid>
      </Grid>
      <ClaimsFixedBottomBar
        {...props}
        onSave={handleSubmit(handleSave)}
        handleNextSubmit={handleSubmit(handleNext)}
        save={isFormEdited}
        next={isFormEdited}
        cancel={isFormEdited}
        handleCancel={handleCancel}
      />
    </>
  );
}
