import React from 'react';
import styles from './ClaimsAcknowledgement.styles';
import isString from 'lodash/isString';
import moment from 'moment'; 


import * as utils from 'utils';

import { Accordion } from 'components';
import { Typography, makeStyles, Grid, Table, TableBody, TableRow } from '@material-ui/core';
import { TableHead, TableCell, Overflow, Skeleton } from 'components';
import { Edit } from '@material-ui/icons';
import config from 'config';

export function LossInfoView({ lossInfo, policyInfo, claimInfo, underWritingInfo, underWritingGroupColumns }) {
    const classes = makeStyles(styles, { name: 'ClaimsAcknowledgement' })();

    const accordionTitle = (title) => {
        return (
            <Typography className={classes.title} variant="body2"> {title}</Typography>
        )
    }
    const actions = [
        {
            id: "1",
            icon: Edit,
            color: "primary",
            onClick: () => { alert("afsddfjh") },
        }
    ]
    const infoCard = (title = "", details = "", isLoading = false) => {
        return (
            <div className={classes.infoCard}>

                <Typography className={classes.infoCardTitle} variant="subtitle2" color="secondary" title={isString(title) ? title : null}>
                    {title}
                </Typography>
                <Typography className={classes.infoCardValue} variant="body2" title={details ? details : null}>
                    {isLoading ? <Skeleton height={40} animation="wave" displayNumber={1} /> : details}

                </Typography>
            </div>
        )

    }
    const renderInfoCard = (items) => {
        return (
            <div className={classes.infoCardContainer}>{items?.map(info => infoCard(info?.title, info?.value, info?.isLoading))}</div>
        )
    }
    const underWritingTabel = (underWriting) => {
        return (
            <>
                <div className={classes.underWritingContainer}>
                    <Grid container direction="column">
                        <Overflow>
                            <Table>
                                <TableHead columns={underWritingGroupColumns} />
                                <TableBody>
                                    {underWriting?.isLoading ?
                                        <TableRow>
                                            <TableCell colSpan={underWritingGroupColumns?.length}>
                                                <Skeleton height={40} animation="wave" displayNumber={3} />
                                            </TableCell>
                                        </TableRow>

                                        :
                                        (underWriting?.items?.map((ugRows, index) => (
                                            <TableRow key={index}>
                                                <TableCell>{ugRows?.groupRef}</TableCell>
                                                <TableCell>{ugRows?.percentage}</TableCell>
                                                <TableCell>{ugRows?.facility}</TableCell>
                                                <TableCell>{ugRows?.facilityRef}</TableCell>
                                                <TableCell>{ugRows?.slipLeader}</TableCell>
                                                <TableCell>{ugRows?.ucr}</TableCell>
                                                <TableCell>{ugRows?.narrative}</TableCell>
                                                <TableCell>{ugRows?.dateValidFrom && moment(ugRows.dateValidFrom).format(config.ui.format.date.slashNumeric)}</TableCell>
                                                <TableCell>{ugRows?.dateValidTo && moment(ugRows.dateValidTo).format(config.ui.format.date.slashNumeric)}</TableCell>
                                            </TableRow>)))
                                    }
                                </TableBody>
                            </Table>
                        </Overflow>

                    </Grid>
                    <div className={classes.underWritingCardContainer}>
                        <div className={classes.infoCardContainer}>
                            <div className={classes.infoCard}>
                                <Typography className={classes.infoCardTitle} variant="h5" color="secondary" >
                                    {utils.string.t('claims.movementInformation.heading')}
                                </Typography>
                                <Typography className={classes.infoCardValue} variant="h6" >
                                    { underWriting?.isLoading   ?  <Skeleton height={40} animation="wave" displayNumber={1} /> : underWriting?.movementType}
                                </Typography>
                            </div>
                            <div className={classes.infoCard}>
                                <Typography className={classes.infoCardTitle} variant="h5" color="secondary">
                                    {utils.string.t('claims.typeOfSettlement.orderBasis')}
                                </Typography>
                                <Typography className={classes.infoCardValue} variant="h6" >
                                    { underWriting?.isLoading   ?  <Skeleton height={40} animation="wave" displayNumber={1} /> : underWriting?.basisOfOrder}

                                </Typography>
                            </div>
                            <div className={classes.infoCard}>
                                <Typography className={classes.infoCardTitle} variant="h5" color="secondary" >
                                    {utils.string.t('claims.typeOfSettlement.order')}

                                </Typography>
                                <Typography className={classes.infoCardValue} variant="h6" >
                                    { underWriting?.isLoading   ?  <Skeleton height={40} animation="wave" displayNumber={1} /> : underWriting?.orderPercentage}

                                </Typography>
                            </div>
                        </div>
                    </div>

                </div>
            </>
        )
    }
    return (
        <>
            <Accordion expanded title={accordionTitle(utils.string.t('claims.lossInformation.title'))} actions={actions}>
                {renderInfoCard(lossInfo)}
            </Accordion>
            <Accordion expanded title={accordionTitle(utils.string.t('claims.claimInformation.policyTitle'))} actions={actions}>
                {renderInfoCard(policyInfo)}
            </Accordion>
            <Accordion expanded title={accordionTitle(utils.string.t('claims.underWritingGroups.title'))} actions={actions}>
                {underWritingTabel(underWritingInfo)}
            </Accordion>
            <Accordion expanded title={accordionTitle(utils.string.t('claims.claimInformation.title'))} actions={actions}>
                {renderInfoCard(claimInfo)}
            </Accordion>
        </>
    )
}
