const styles = (theme) => ({
  drawer: ({ isFromDashboard }) => ({
    flexDirection: 'row',
    maxWidth: theme.mixins.drawer.default.width,
    marginTop: isFromDashboard ? theme.spacing(3) : 0,
    marginRight: isFromDashboard ? theme.spacing(2) : 0,
    overflowX: 'hidden',
  }),
  drawerExpanded: ({ isFromDashboard }) => ({
    width: theme.mixins.drawer.expanded.width,
    flexDirection: 'row',
    marginTop: isFromDashboard ? theme.spacing(3) : 0,
  }),
});

export default styles;
