const styles = (theme) => ({
  root: ({ width }) => ({
    width: width,
    margin: theme.spacing(1.25, 2, 1.25, 0),
  }),
  subRoot: ({ showHover }) => ({
    display: 'flex',
    alignItems: 'center',
    backgroundColor: showHover ? '#f5f5f5' : 'transperent',
  }),
  detailsContainer: {
    width: '90%',
  },
  title: ({ textAlign }) => ({
    fontWeight: 600,
    textAlign: textAlign,
    margin: theme.spacing(0, 0, 1.25, 0),
    color: 'rgba(0, 0, 0, 0.87)!important',
  }),
  details: {
    textAlign: 'left',
    margin: theme.spacing(0, 0, 0, 0.25),
    color: 'inherit!important',
    fontWeight: 400,
    lineHeight: theme.typography.pxToRem(16),
  },
  link: {
    marginLeft: theme.spacing(1),
  },
  clipboard: ({ showHover }) => ({
    textAlign: 'right',
    // visibility: showHover ? 'visible' : 'hidden',
    visibility: 'visible',
  }),
});

export default styles;
