const styles = (theme) => ({
  drawer: ({ isFromDashboard }) => ({
    ...theme.mixins.drawer.default,
    flexDirection: 'row',
    marginTop: isFromDashboard ? theme.mixins.header.default.minHeight : 0,
    overflow: 'hidden',
    transition: theme.transitions.create(['transform']),
    transform: 'translateX(30px)',

    '&:hover': {
      transform: 'translateX(4px)',
    },
  }),
  drawerExpanded: ({ isFromDashboard }) => ({
    width: theme.mixins.drawer.expanded.width,
    overflowX: 'hidden',
    overflowY: 'auto',
    transform: 'translateX(0)',

    [theme.breakpoints.down('md')]: {
      minWidth: theme.mixins.drawer.expanded.minWidth,
    },

    '&:hover': {
      transform: 'translateX(0)',
    },
  }),
  button: {
    paddingLeft: 4,
    paddingRight: 4,
    marginLeft: -5,
  },
  buttonExpanded: {
    marginLeft: -8,
  },
  iconArrow: {
    marginLeft: 4,
  },
  iconArrowExpanded: {
    marginLeft: 0,
  },
  iconDocument: {
    marginLeft: 2,
    fontSize: theme.typography.pxToRem(24),
  },
});

export default styles;
