import React, { useState } from 'react';
import styles from './LossDetail.styles';
import * as utils from 'utils';

//app
import { Layout, Accordion } from 'components';

//mui
import { makeStyles, Typography } from '@material-ui/core';
import { Edit } from '@material-ui/icons';

export default function LossDetailView() {
  const classes = makeStyles(styles, { name: 'LossDetail' })();
  const [accordionsExpandStatus, setAccordiansStatus] = useState({
    [utils.string.t('loss.lossInformation.title')]: true,
    [utils.string.t('loss.relatedClaims.title')]: true,
  });
  const actions = () => {
    return [
      {
        id: '1',
        icon: Edit,
        color: 'primary',
        onClick: () => {
          alert('coming soon');
        },
      },
    ];
  };
  const accordionTitle = (title) => {
    return (
      <Typography className={classes.title} variant="body2">
        {' '}
        {title}
      </Typography>
    );
  };
  const setAccordiansIsExpanded = (cardName, isExpand) => {
    const accordionsStatus = { ...accordionsExpandStatus };
    accordionsStatus[cardName] = isExpand;
    setAccordiansStatus(accordionsStatus);
  };
  return (
    <div className={classes.wrapper}>
      <Layout main padding>
        <Accordion
          expanded={accordionsExpandStatus[utils.string.t('loss.lossInformation.title')]}
          title={accordionTitle(utils.string.t('loss.lossInformation.title'))}
          actions={actions()}
          onChange={(event, isExpanded) => setAccordiansIsExpanded(utils.string.t('loss.lossInformation.title'), isExpanded)}
        >
          test
        </Accordion>
        <Accordion
          expanded={accordionsExpandStatus[utils.string.t('loss.relatedClaims.title')]}
          title={accordionTitle(utils.string.t('loss.relatedClaims.title'))}
          onChange={(event, isExpanded) => setAccordiansIsExpanded(utils.string.t('loss.relatedClaims.title'), isExpanded)}
        >
          test
        </Accordion>
      </Layout>
    </div>
  );
}
