import React from 'react';
import PropTypes from 'prop-types';
import { useSelector } from 'react-redux';
import * as Yup from 'yup';
import moment from 'moment';

// app
import styles from './ClaimsEnterLossInformation.styles';
import { ClaimsEnterLossInformationView } from './ClaimsEnterLossInformation.view';
import { selectLossId } from 'stores';
import * as utils from 'utils';

// mui
import { makeStyles } from '@material-ui/core';

ClaimsEnterLossInformationForm.propTypes = {
  isAllStepsCompleted: PropTypes.bool.isRequired,
  activeStep: PropTypes.number.isRequired,
  lastStep: PropTypes.bool.isRequired,
  handleCancel: PropTypes.func.isRequired,
  handleFinish: PropTypes.func.isRequired,
  handleNext: PropTypes.func.isRequired,
  handleBack: PropTypes.func.isRequired,
  handleSave: PropTypes.func.isRequired,
};

export function ClaimsEnterLossInformationForm(props) {
  const classes = makeStyles(styles, { name: 'ClaimsEnterLossInformation' })();
  const lossInformation = useSelector(selectLossId);
  const hasLossRef = Boolean(lossInformation.lossRef);

  const fields = [
    {
      name: 'lossRef',
      type: 'text',
      value: lossInformation?.lossRef || '',
      muiComponentProps: {
        disabled: true,
      },
    },
    {
      name: 'catCodesID',
      type: 'autocompletemui',
      options: props.formattedCatCodes,
      optionKey: 'id',
      optionLabel: 'name',
      value:
        props.formattedCatCodes.find((c) => c.id?.toString() === lossInformation?.catCodesID?.toString()) || props.formattedCatCodes[0],
      muiComponentProps: {
        classes: {
          root: classes.catCodeSelect,
        },
      },
    },
    {
      type: 'datepicker',
      name: 'fromDate',
      value: lossInformation?.fromDate || null,
      icon: 'TodayIcon',
      muiComponentProps: {
        fullWidth: true,
        classes: {
          root: classes.datepicker,
        },
      },
      validation: Yup.date()
        .test('from', utils.string.t('claims.lossInformation.validation.greaterThenDate'), function (value) {
          return value && this.options.parent.toDate ? moment(value).isSameOrBefore(this.options.parent.toDate) : true;
        })
        .when('catCodesID', {
          is: (val) => val !== null && val?.id !== '0',
          then: Yup.date().nullable().required(utils.string.t('claims.lossInformation.validation.fromDate')),
          otherwise: Yup.date().nullable(),
        }),
      outputFormat: 'iso',
    },
    {
      type: 'datepicker',
      name: 'toDate',
      value: lossInformation?.toDate || null,
      muiComponentProps: {
        fullWidth: true,
        classes: {
          root: classes.datepicker,
        },
      },
      validation: Yup.date()
        .test('from', utils.string.t('claims.lossInformation.validation.lesserThenDate'), function (value) {
          return value && this.options.parent.fromDate ? moment(value).isSameOrAfter(this.options.parent.fromDate) : true;
        })
        .when('catCodesID', {
          is: (val) => val !== null && val?.id !== '0',
          then: Yup.date().nullable().required(utils.string.t('claims.lossInformation.validation.toDate')),
          otherwise: Yup.date().nullable(),
        }),
      outputFormat: 'iso',
    },
    {
      name: 'lossName',
      type: 'textarea',
      value: lossInformation?.lossName || '',
      validation: Yup.string()
        .max(50, utils.string.t('claims.lossInformation.validation.maxLossName'))
        .required(utils.string.t('claims.lossInformation.validation.lossName')),
      muiComponentProps: {
        inputProps: { maxLength: 51 },
        multiline: true,
        rows: 5,
        rowsMax: 6,
      },
    },
    {
      name: 'lossDescription',
      type: 'textarea',
      value: lossInformation?.lossDescription || '',
      validation: Yup.string()
        .max(350, utils.string.t('claims.lossInformation.validation.maxLossDescription'))
        .required(utils.string.t('claims.lossInformation.validation.lossDescription')),
      muiComponentProps: {
        inputProps: { maxLength: 351 },
        multiline: true,
        rows: 8,
        rowsMax: 8,
      },
    },
    {
      type: 'datepicker',
      name: 'firstContactDate',
      icon: 'TodayIcon',
      value: lossInformation?.firstContactDate || utils.date.today(),
      muiComponentProps: {
        fullWidth: true,
      },
      validation: Yup.date().max(new Date(), utils.string.t('claims.lossInformation.validation.futureDate')),
      outputFormat: 'iso',
    },
    {
      type: 'time',
      name: 'firstContactTime',
      icon: 'AccessTimeIcon',
      value: utils.string.t('format.date', { value: { date: lossInformation?.firstContactDate || new Date(), format: 'HH:mm' } }),
      muiComponentProps: {
        fullWidth: true,
      },
    },
  ];

  return <ClaimsEnterLossInformationView {...props} fields={fields} hasLossRef={hasLossRef} lossInformation={lossInformation} />;
}
