import React,{useEffect, useState} from 'react';
import PropTypes from 'prop-types';
import {useDispatch, useSelector } from 'react-redux';
import { RegisterNewLossFixedBottomBar, ClaimsEnterLossInformation, LinkSearchPolicy, RegisterClaimCardInformation } from 'modules';

import {
  selectCatCodes,
  getCatCodes,
  getComplexityValues, 
  getReferralValues,  
  getBEAdjuster, 
  getClaimBordereauPeriods,
  getPriorityLevels
} from 'stores';
import * as utils from 'utils';
import styles from './ClaimsRegisterNewLoss.style';
import * as constants from 'consts';

import { LinearProgress, Stepper, Step, StepLabel, makeStyles, } from '@material-ui/core';

ClaimsRegisterNewLossView.propTypes = {
  activeStep: PropTypes.number.isRequired,
  setActiveStep: PropTypes.func.isRequired,
  handleStep: PropTypes.func.isRequired,
  steps: PropTypes.array.isRequired,
  progress: PropTypes.number.isRequired,
  isFormsEdited: PropTypes.array.isRequired,
  isAllStepsCompleted: PropTypes.bool.isRequired,
  handleFormStatus: PropTypes.func.isRequired,
  handleNext: PropTypes.func.isRequired,
  handleBack: PropTypes.func.isRequired,
  handleSave: PropTypes.func.isRequired,
  setFormEditedStatus: PropTypes.func.isRequired,
  setSaveStatus: PropTypes.func.isRequired,
  saveStatus: PropTypes.number.isRequired,
};

export default function ClaimsRegisterNewLossView({ activeStep, setActiveStep, handleStep, steps, progress, isFormsEdited, isAllStepsCompleted, handleNext, handleFormStatus, handleBack, handleSave, setFormEditedStatus, saveStatus, setSaveStatus }) {
  const classes = makeStyles(styles, { name: 'ClaimsRegisterNewLoss' })();
  const [policyRef, setPolicyRef] = useState(false);
  const catCodes = useSelector(selectCatCodes);
  const dispatch = useDispatch();
  const getAllReferentialData = () => {
    if (utils.generic.isInvalidOrEmptyArray(catCodes)) {
      dispatch(getCatCodes());
    }
  };

  useEffect(() => {
    getAllReferentialData();
    dispatch(getComplexityValues());
    dispatch(getReferralValues());
    dispatch(getBEAdjuster());
    dispatch(getClaimBordereauPeriods());
    dispatch(getPriorityLevels());
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const stepContent = (index) => {
    switch (index) {
      case 0:
        return (
          <div>
            <ClaimsEnterLossInformation
                policyRef={policyRef}
                isAllStepsCompleted={isAllStepsCompleted}
                handleNext={() => handleNext(index)}
                handleSave={() => { handleSave(index) }}
                activeStep={activeStep}
                lastStep={false}
                context={constants.DMS_CONTEXT_LOSS}
                saveStatus={saveStatus}
                handleBack={handleBack}
            />
        </div>
        );
      case 1:
        return (

          <div>
            <LinkSearchPolicy
                policyRef={policyRef}
                setPolicyRef={setPolicyRef}
                isFormsEdited={isFormsEdited}
                setFormEditedStatus={setFormEditedStatus}
                activeStep={activeStep}
                setActiveStep={setActiveStep}
                isAllStepsCompleted={isAllStepsCompleted}
                handleBack={handleBack}
                handleSave={handleSave}
                handleNext={handleNext}
                save={isFormsEdited[index]?.formEditedStatus}
                index={index}
                saveStatus={saveStatus}
                setSaveStatus={setSaveStatus}
            />

            {/* <RegisterNewLossFixedBottomBar
              activeStep={activeStep}
              isAllStepsCompleted={isAllStepsCompleted}
              handleBack={() => { handleBack(index) }}
              handleSave={() => { handleSave(index) }}
              handleNextSubmit={() => handleNext(index)}
              save={isFormsEdited?.[index]?.formEditedStatus}
            /> */}
          </div>
        );
      case 2:
        return (
          <div>
            <RegisterClaimCardInformation/>
            <RegisterNewLossFixedBottomBar
              activeStep={activeStep}
              isAllStepsCompleted={isAllStepsCompleted}
              handleBack={() => { handleBack(index) }}
              handleSave={() => { handleSave(index) }}
              handleNextSubmit={() => handleNext(index)}
              save={isFormsEdited?.[index]?.formEditedStatus}
            />
          </div>
        );
      case 3:
        return (
          <div>
            <h1 onClick={() => handleFormStatus(index)}> {isFormsEdited?.[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
            <h1 > {isFormsEdited?.[index]?.isSubmitted ? "isSubmitted : true" : "isSubmitted : false"}</h1>

            <RegisterNewLossFixedBottomBar
              activeStep={activeStep}
              isAllStepsCompleted={isAllStepsCompleted}
              handleBack={() => { handleBack(index) }}
              handleSave={() => { handleSave(index) }}
              handleNextSubmit={() => handleNext(index)}
              save={isFormsEdited?.[index]?.formEditedStatus}
            />
          </div>
        );
      case 4:
        return (
          <div>
            <h1 onClick={() => handleFormStatus(index)}> {isFormsEdited?.[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
            <h1 > {isFormsEdited?.[index]?.isSubmitted ? "isSubmitted : true" : "isSubmitted : false"}</h1>

            <RegisterNewLossFixedBottomBar
              lastStep={true} activeStep={activeStep}
              isAllStepsCompleted={isAllStepsCompleted}
              handleBack={() => { handleBack(index) }}
              handleSave={() => { handleSave(index) }}
              handleNextSubmit={() => handleNext(index)}
              save={isFormsEdited?.[index]?.formEditedStatus}
            />
          </div>
        );
      default:
        return (
          <div>
            <h1 onClick={() => handleFormStatus(index)}> {isFormsEdited?.[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
            <h1 > {isFormsEdited?.[index]?.isSubmitted ? "isSubmitted : true" : "isSubmitted : false"}</h1>
            <RegisterNewLossFixedBottomBar
              activeStep={activeStep}
              onSave={() => { }}
              isAllStepsCompleted={isAllStepsCompleted}
              handleSave={() => { handleSave(index) }}
              handleNextSubmit={() => handleNext(index)}
              save={isFormsEdited?.[index]?.formEditedStatus}
            />
          </div>
        );
    }
  };

  return (
    <div>
      <Stepper alternativeLabel activeStep={activeStep} data-testid="LinearStepper"
      >
        {steps.map((label, index) => (
          <Step key={label} onClick={() => handleStep(index)}>
            <StepLabel
              StepIconProps={{
                classes: { root: classes.iconContainer },
              }}
            >
              {label}
            </StepLabel>
          </Step>
        ))}
      </Stepper>
      <LinearProgress variant="determinate" value={progress} className={classes.linearProgress} />
      {stepContent(activeStep)}
    </div>


  );
};
