import { authLogout, addLoader, removeLoader } from 'stores';
import * as utils from 'utils';

import get from 'lodash/get';

export const getClaimsBasicInformation =
  ({ claimId, claimRefParams, sourceIdParams, divisionIDParams, viewLoader = true }) =>
  (dispatch, getState) => {
    // prettier-ignore
    const { user: { auth }, config: { vars: { endpoint } } } = getState();
    const defaultError = {
      file: 'stores/claims.actions.getClaimsBasicInformation',
    };

    const state = getState();
    const claimSubmissionId = claimId ? claimId : get(state, 'claims.claimsInformation.claimID') || '';
    const sourceId = sourceIdParams ? sourceIdParams : get(state, 'claims.claimsInformation.sourceID');
    const claimRef = claimRefParams ? claimRefParams : get(state, 'claims.claimsInformation.claimReference');
    const divisionId = divisionIDParams ? divisionIDParams : get(state, 'claims.claimsInformation.departmentID');
    const isBasicDetail = true;
    dispatch(getClaimsBasicInformationRequest(claimSubmissionId));
    viewLoader && dispatch(addLoader('getClaimsBasicInformation'));
    return utils.api
      .get({
        token: auth.accessToken,
        endpoint: endpoint.claimService,
        path: `api/data/claims/${claimSubmissionId}/basic-detail`,
        params: {
          sourceId,
          claimRef,
          divisionId,
          isBasicDetail
        },
      })
      .then((response) => utils.api.handleResponse(response))
      .then((data) => {
        dispatch(getClaimsBasicInformationSuccess(data.data));
        return data;
      })
      .catch((err) => {
        dispatch(getClaimsBasicInformationFailure(err, defaultError));
        utils.api.handleUnauthorized(err, dispatch, authLogout);
        return err;
      })
      .finally(() => {
        viewLoader && dispatch(removeLoader('getClaimsBasicInformation'));
      });
  };

export const getClaimsBasicInformationRequest = (data) => {
  return {
    type: 'CLAIMS_BASIC_INFORMATION_GET_REQUEST',
    payload: { data },
  };
};

export const getClaimsBasicInformationSuccess = (data) => {
  return {
    type: 'CLAIMS_BASIC_INFORMATION_GET_SUCCESS',
    payload: data,
  };
};

export const getClaimsBasicInformationFailure = (data) => {
  return {
    type: 'CLAIMS_BASIC_INFORMATION_GET_FAILURE',
    payload: data,
  };
};
