import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
// app

import { makeStyles, Divider } from '@material-ui/core';
import LinearProgress from '@material-ui/core/LinearProgress';

import styles from './ClaimsRegisterNewLoss.style';
import { RegisterNewLossFixedBottomBar, LinkSearchPolicy, } from 'modules';


const ClaimsRegisterNewLossView = ({ activeStep, handleStep, steps, progress, isFormsEdited, isAllStepsCompleted, handleNext, handleFormStatus, handleBack, handleSave }) => {
    const dispatch = useDispatch();
    const [policyRef, setPolicyRef] = useState(false);
    const classes = makeStyles(styles, { name: 'ClaimsRegisterNewLoss' })();
    const stepContent = (index) => {
        switch (index) {
            case 0:
                return (
                    <div>
                        <h1 onClick={() => handleFormStatus(index)}> {isFormsEdited[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
                        <Divider />
                        <RegisterNewLossFixedBottomBar
                            activeStep={activeStep}
                            isAllStepsCompleted={isAllStepsCompleted}
                            handleSave={() => { handleSave(index) }}
                            handleNextSubmit={() => handleNext(index)}
                            save={isFormsEdited[index]?.formEditedStatus}
                        />
                    </div>
                );
            case 1:
                return (

                    <div>
                        <h1 onClick={() => handleFormStatus(index)}>{isFormsEdited[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
                        <LinkSearchPolicy
                            policyRef={policyRef}
                            setPolicyRef={setPolicyRef}
                        />
                        <RegisterNewLossFixedBottomBar
                            activeStep={activeStep}
                            isAllStepsCompleted={isAllStepsCompleted}
                            handleBack={() => { handleBack(index) }}
                            handleSave={() => { handleSave(index) }}
                            handleNextSubmit={() => handleNext(index)}
                            save={isFormsEdited[index]?.formEditedStatus}
                        />
                    </div>
                );
            case 2:
                return (
                    <div>
                        <h1 onClick={() => handleFormStatus(index)}>{isFormsEdited[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
                        <RegisterNewLossFixedBottomBar
                            activeStep={activeStep}
                            isAllStepsCompleted={isAllStepsCompleted}
                            handleBack={() => { handleBack(index) }}
                            handleSave={() => { handleSave(index) }}
                            handleNextSubmit={() => handleNext(index)}
                            save={isFormsEdited[index]?.formEditedStatus}
                        />
                    </div>
                );
            case 3:
                return (
                    <div>
                        <h1 onClick={() => handleFormStatus(index)}>{isFormsEdited[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
                        <RegisterNewLossFixedBottomBar
                            activeStep={activeStep}
                            isAllStepsCompleted={isAllStepsCompleted}
                            handleBack={() => { handleBack(index) }}
                            handleSave={() => { handleSave(index) }}
                            handleNextSubmit={() => handleNext(index)}
                            save={isFormsEdited[index]?.formEditedStatus}
                        />
                    </div>
                );
            case 4:
                return (
                    <div>
                        <h1 onClick={() => handleFormStatus(index)}>{isFormsEdited[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
                        <RegisterNewLossFixedBottomBar
                            lastStep={true} activeStep={activeStep}
                            isAllStepsCompleted={isAllStepsCompleted}
                            handleBack={() => { handleBack(index) }}
                            handleSave={() => { handleSave(index) }}
                            handleNextSubmit={() => handleNext(index)}
                            save={isFormsEdited[index]?.formEditedStatus}
                        />
                    </div>
                );
            default:
                return (
                    <div>
                        <h1 onClick={() => handleFormStatus(index)}>{isFormsEdited[index]?.formEditedStatus ? "Dirty : true" : "Dirty : false"}</h1>
                        <RegisterNewLossFixedBottomBar
                            activeStep={activeStep}
                            onSave={() => { }}
                            isAllStepsCompleted={isAllStepsCompleted}
                            handleSave={() => { handleSave(index) }}
                            handleNextSubmit={() => handleNext(index)}
                            save={isFormsEdited[index]?.formEditedStatus}
                        />
                    </div>
                );
        }
    };

    return (
        <div>
            <Stepper alternativeLabel activeStep={activeStep}>
                {steps.map((label, index) => (
                    <Step key={label} onClick={() => handleStep(index)}>
                        <StepLabel
                            StepIconProps={{
                                classes: { root: classes.iconContainer },
                            }}
                        >
                            {label}
                        </StepLabel>
                    </Step>
                ))}
            </Stepper>
            <LinearProgress variant="determinate" value={progress} className={classes.linearProgress} />
            {
                stepContent(activeStep)
            }

        </div>
    );
};

export default ClaimsRegisterNewLossView;
