import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import get from 'lodash/get';
import moment from 'moment';

import * as utils from 'utils';
import { ClaimsAcknowledgementView } from './ClaimsAcknowledgement.view';
import {
  selectClaimIdUnderProgress,
  selectClaimsInformation,
  selectLossId,
  selectClaimsPolicyInformation,
  getClaimsPreviewInformation,
  selectClaimsInterest,
  selectClaimInfoIsLoading,
  selectCatCodes,
  getLossInformation,
  selectLossInfoIsLoading,
  getPolicyInformation,
  selectPolicyInfoIsLoading
} from 'stores';
import config from 'config'


export default function ClaimsAcknowledgement(props) {
  const claimData = useSelector(selectClaimIdUnderProgress);
  const claimId = get(claimData, 'claimID') ?? '';
  const claimsInformation = useSelector(selectClaimsInformation);
  const [generateLetterChecked, setGenerateLetterChecked] = useState(false);
  const lossInformation = useSelector(selectLossId);
  const policyInformation = useSelector(selectClaimsPolicyInformation);
  const interests = useSelector(selectClaimsInterest)?.items;
  const isClaimInfoLoading = useSelector(selectClaimInfoIsLoading)
  const isLossInfoLoading = useSelector(selectLossInfoIsLoading)
  const isPolicyInfoLoading = useSelector(selectPolicyInfoIsLoading)
  const catCodes = useSelector(selectCatCodes);
  const catCode = catCodes.find((item) => Number(item.id) === lossInformation.catCodesID)?.description;
  const underWritingInfo = {
    items: claimsInformation?.policyUnderWritingGroupDtoList,
    isLoading: isClaimInfoLoading,
    basisOfOrder: claimsInformation?.basisOfOrder ? "Our Share" : "100%",
    movementType: claimsInformation?.movementType,
    orderPercentage: claimsInformation?.orderPercentage
  }
  const dispatch = useDispatch()
  useEffect(() => {
    dispatch(getLossInformation(claimData?.data.lossDetailID, claimData?.data.sourceID, claimData?.data.divisionID, claimData?.data.claimReference));
    dispatch(getClaimsPreviewInformation(claimId, claimData?.data?.claimReference, claimData?.data?.sourceID, claimData?.data?.divisionID || claimData?.data?.departmentID));
    dispatch(getPolicyInformation())
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const lossInfo = [
    { title: utils.string.t('claims.lossInformation.ref'), value: lossInformation?.lossRef },
    { title: utils.string.t('claims.lossInformation.fromDate'), value: lossInformation?.fromDate && moment(lossInformation?.fromDate).format(config.ui.format.date.slashNumeric) },
    { title: utils.string.t('claims.lossInformation.toDate'), value: lossInformation?.toDate && moment(lossInformation?.toDate).format(config.ui.format.date.slashNumeric) },
    { title: utils.string.t('claims.lossInformation.name'), value: lossInformation?.lossName },
    { title: utils.string.t('claims.lossInformation.dateAndTime'), value: lossInformation?.firstContactDate && moment(lossInformation?.firstContactDate).format(config.ui.format.date.slashNumericDateAndTime) },
    { title: utils.string.t('claims.lossInformation.assignedTo'), value: lossInformation?.lossRef },
    { title: utils.string.t('claims.lossInformation.details'), value: lossInformation?.lossDescription },
    { title: utils.string.t('claims.lossInformation.catCode'), value: catCode },
  ]
  const claimInfo = [
    { title: utils.string.t('claims.claimInformation.claimRef'), value: claimsInformation?.claimReference },
    { title: utils.string.t('claims.claimInformation.claimReceivedDateTime'), value: claimsInformation?.claimReceivedDate && moment(claimsInformation?.claimReceivedDate).format(config.ui.format.date.slashNumeric) },
    { title: utils.string.t('claims.claimInformation.claimant'), value: claimsInformation?.claimantName },
    { title: utils.string.t('claims.columns.claimsManagement.lossDateFrom'), value: claimsInformation?.lossFromDate && moment(claimsInformation?.lossToDate).format(config.ui.format.date.slashNumericDateAndTime) },
    { title: utils.string.t('claims.columns.claimsManagement.lossDateTo'), value: claimsInformation?.lossToDate && moment(claimsInformation?.lossToDate).format(config.ui.format.date.slashNumericDateAndTime) },
    { title: utils.string.t('claims.columns.claimsManagement.lossDateQualifier'), value: claimsInformation?.lossQualifierName },
    { title: utils.string.t('claims.claimInformation.location') + '*', value: claimsInformation?.location },
    { title: utils.string.t('claims.claimInformation.fguNarrative'), value: claimsInformation?.fgunarrative },
    { title: utils.string.t('claims.claimInformation.priority'), value: claimsInformation?.priorityDescription },
    {
      title: utils.string.t('claims.claimInformation.adjustorType'), value: claimsInformation?.beAdjusterID === 0
        ? utils.string.t('claims.claimInformation.nonBeAdjuster')
        : utils.string.t('claims.claimInformation.beAdjuster')
    },
    { title: utils.string.t('claims.claimInformation.adjustorName'), value: claimsInformation?.beAdjusterID !== 0 ? claimsInformation.adjusterName : claimsInformation.nonBEAdjusterName },
    { title: utils.string.t('claims.claimInformation.adjustorRef'), value: claimsInformation?.adjusterReference },
    { title: utils.string.t('claims.claimInformation.complexity') + '*', value: claimsInformation?.complexity },
    { title: utils.string.t('claims.claimInformation.referral') + '*', value: claimsInformation?.referralValue },
    { title: utils.string.t('claims.claimInformation.rfiResponse') + '*', value: claimsInformation?.referralResponseDescription },
    { title: utils.string.t('claims.claimInformation.bordereauClaim') + '*', value: claimsInformation?.bordereauClaim === 1 ? "Yes" : "No" },
    { title: utils.string.t('claims.claimInformation.bordereauPeriod') + '*', value: claimsInformation?.bordereauClaim === 1 ? claimsInformation?.bordereauPeriod : "NA" },
    { title: utils.string.t('claims.claimInformation.claimStatus') + '*', value: claimsInformation?.claimStatus },
  ]
  const policyInfo = [
    { title: utils.string.t('claims.columns.claimsManagement.policyRef'), value: policyInformation?.policyRef, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.policyType'), value: policyInformation?.policyType, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.searchPolicy.columns.policyStatus'), value: policyInformation?.policyStatus, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.company'), value: policyInformation?.company, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.division'), value: policyInformation?.division, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.searchPolicy.columns.riskDetails'), value: policyInformation?.policyNote, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.client'), value: policyInformation?.client, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.insured'), value: policyInformation?.insured, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.reinsured'), value: policyInformation?.reinsured, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.searchPolicy.columns.inceptionDate'), value: policyInformation?.inceptionDate && moment(policyInformation?.inceptionDate).format(config.ui.format.date.slashNumericDateAndTime), isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.searchPolicy.columns.expiryDate'), value: policyInformation?.expiryDate && moment(policyInformation?.expiryDate).format(config.ui.format.date.slashNumericDateAndTime), isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.columns.claimsManagement.interest'), value: interests?.find((item) => item.policyInterestID === claimsInformation.policyInterestID)?.description, isLoading: isClaimInfoLoading },
    { title: utils.string.t('claims.claimInformation.certificateInceptionDate'), value: claimsInformation?.isBordereau ? claimsInformation?.certificateInceptionDate && moment(claimsInformation?.certificateInceptionDate).format(config.ui.format.date.slashNumericDateAndTime) : "NA", isLoading: isClaimInfoLoading },
    { title: utils.string.t('claims.claimInformation.certificateExpiryDate'), value: claimsInformation?.isBordereau ? claimsInformation?.certificateExpiryDate && moment(claimsInformation?.certificateExpiryDate).format(config.ui.format.date.slashNumericDateAndTime) : "NA", isLoading: isClaimInfoLoading },
    { title: utils.string.t('claims.claimInformation.certificateNumber'), value: claimsInformation?.isBordereau ? claimsInformation?.certificateNumber : "NA", isLoading: isClaimInfoLoading },
    { title: utils.string.t('claims.claimInformation.originalCurrency'), value: policyInformation?.originalCurrency, isLoading: isPolicyInfoLoading },
    { title: utils.string.t('claims.claimInformation.settlementCurrency'), value: policyInformation?.settlementCurrency, isLoading: isPolicyInfoLoading },
  ]
  const underWritingGroupColumns = [
    {
      id: 'groupRef',
      label: utils.string.t('claims.underWritingGroups.groupRef'),
    },
    {
      id: 'percentage',
      label: utils.string.t('claims.underWritingGroups.percentage'),
    },
    {
      id: 'facility',
      label: utils.string.t('claims.underWritingGroups.facility'),
    },
    {
      id: 'facilityRef',
      label: utils.string.t('claims.underWritingGroups.facilityRef'),
    },
    {
      id: 'slipLeader',
      label: utils.string.t('claims.underWritingGroups.slipLeader'),
    },
    {
      id: 'ucr',
      label: utils.string.t('claims.underWritingGroups.ucr'),
    },
    {
      id: 'narrative',
      label: utils.string.t('claims.underWritingGroups.narrative'),
    },
    {
      id: 'dateValidFrom',
      label: utils.string.t('claims.underWritingGroups.dateValidFrom'),
    },
    {
      id: 'dateValidTo',
      label: utils.string.t('claims.underWritingGroups.dateValidTo'),
    },
  ];

  return (
    <>
      <ClaimsAcknowledgementView
        {...props}
        generateLetterChecked={generateLetterChecked}
        setGenerateLetterChecked={setGenerateLetterChecked}
        handleClaim={props.handleClaim}
        handleFinish={props.handleFinish}
        handleStep={props.handleStep}
        claimId={claimId}
        claimsInformation={claimsInformation}
        lossInfo={lossInfo?.map(info => ({
          ...info,
          isLoading: isLossInfoLoading
        }))}
        policyInfo={policyInfo}
        claimInfo={claimInfo.map(info => ({
          ...info,
          isLoading: isClaimInfoLoading
        }))}
        underWritingInfo={underWritingInfo}
        underWritingGroupColumns={underWritingGroupColumns}
      />
    </>
  );
}
