import get from 'lodash/get';
import xorBy from 'lodash/xorBy';

// app
import config from 'config';
import * as constants from 'consts';
import * as utils from 'utils';

const initialState = {
  isChoosing: false,
  sectionEnabledUG: false,
  claimsStepper: 0,
  catCodes: [],
  claimsAssociateWithLoss: [],
  claimantNames: [],
