import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';

// app
import styles from './RegisterClaimCardInformation.styles';
import * as utils from 'utils';
import {
  FormContainer,
  FormText,
  FormGrid,
  FormLabel,
  FormDate,
  FormAutocompleteMui,
  FormRadio,
  FormCheckbox,
  FormFields,
  FormLegend,
} from 'components';
import { ClaimsUploadViewSearchDocs, RegisterNewLossFixedBottomBar } from 'modules';
import { postEditClaimDetailsInformation } from 'stores';
import { CLAIM_LOSS_DATE_DISABLED_QUALIFIERS, DMS_CONTEXT_CLAIM, DMS_DOCUMENT_TYPE_SECTION_KEYS } from 'consts';

// mui
import { makeStyles, Box, Grid } from '@material-ui/core';
RegisterClaimCardInformationView.prototype = {
  claimantNames: PropTypes.array.isRequired,
  referralValues: PropTypes.array.isRequired,
  lossQualifiers: PropTypes.object,
  resetKey: PropTypes.number,
  hasClaimRef: PropTypes.bool.isRequired,
  claimsDocsList: PropTypes.array,
  parentLossRef: PropTypes.string,
};
export function RegisterClaimCardInformationView({
  fields,
  referralValues,
  claimsDocsList,
  claimRefId,
  claimData,
  policyInformation,
  complexityValues,
  index,
  handleNext,
  handleSave,
  handleBack,
  activeStep,
  isAllStepsCompleted,
  isBordereauChecked,
  lossQualifiers,
  handleFormStatus,
  lossInformation,
  parentLossRef,
}) {
  const classes = makeStyles(styles, { name: 'EnterClaimCardInformation' })();
  const dispatch = useDispatch();
  const [dateValidation, setDateValidation] = useState(false);
  const [refResponseDisable, setRefResponseDisable] = useState(true);
  const [refValuesDisable, setRefValueDisable] = useState(false);
  const defaultValues = utils.form.getInitialValues(fields);
  const validationSchema = utils.form.getValidationSchema(fields);

  const {
    control,
    errors,
    setValue,
    handleSubmit,
    watch,
    formState: { isDirty },
  } = useForm({
    defaultValues,
    ...(validationSchema && { resolver: yupResolver(validationSchema) }),
  });

  const lossQualifierID = watch('lossQualifierID')?.id;
  const complexValues = watch('complexity');
  const refValues = watch('referral');

  const getRefValues = (referralValue) => {
    if (referralValue) {
      const getReferralValue = referralValues.find(
        (item) => item.complexityRulesID === referralValue?.complexityRulesID
      )?.complexityRulesValue;
      const refValuesList = ['Referral Not Required', 'Referral Not Required: Assign to Myself', 'Unsure'];
      const refResponseState = refValuesList.includes(getReferralValue);
      setRefResponseDisable(refResponseState);
      if (refResponseState) {
        setValue('rfiResponse', null);
      }
    } else if (referralValue === null || !referralValue) {
      setRefResponseDisable(true);
      setValue('rfiResponse', null);
    }
  };

  const handleSubmitForm = (values) => {
    dispatch(postEditClaimDetailsInformation(values)).then((response) => {
      if (response) handleNext(index);
    });
  };

  const handleSaveForm = (values) => {
    dispatch(postEditClaimDetailsInformation(values)).then((response) => {
      if (response) handleSave();
    });
  };

  useEffect(() => {
    isDirty && handleFormStatus();
  }, [isDirty]); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    if (lossQualifierID) {
      const dateDisabledLossQualifiers = lossQualifiers
        .filter((item) => CLAIM_LOSS_DATE_DISABLED_QUALIFIERS.find((itr) => itr === item.name))
        .map((item) => item.id);
      const isRequired = !dateDisabledLossQualifiers.includes(lossQualifierID);
      setDateValidation(!isRequired);
      if (!dateDisabledLossQualifiers.includes(lossQualifierID)) {
        lossInformation.fromDate ? setValue('fromDate', lossInformation.fromDate) : setValue('fromDate', null);
        lossInformation.toDate ? setValue('toDate', lossInformation.toDate) : setValue('toDate', null);
      } else {
        setValue('fromDate', null);
        setValue('toDate', null);
      }
    }
    if (complexValues) {
      const getComplexityStatus =
        complexityValues.find((item) => item.complexityRulesID === complexValues?.complexityRulesID)?.complexityRulesValue ===
        'Referral Required';
      setRefValueDisable(getComplexityStatus);
      getRefValues(refValues);
    } else if (complexValues === null) {
      setRefValueDisable(false);
    }

    console.log('jenea');
  }, [lossQualifierID, complexValues]); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <>
      <Box m={3} mb={0} data-testid="register-claim-card-information">
        <Grid container direction="row" justify="space-between" alignItems="center">
          <Grid item className={classes.legendContainer}>
            <FormLegend text={utils.string.t('claims.claimInformation.title')} nestedClasses={{ root: classes.formLegend }} />
          </Grid>
          <FormLabel
            label={`${claimRefId?.claimReference || claimRefId?.claimRef} | ${policyInformation.policyRef} | ${policyInformation.insured}`}
            align="left"
          />
        </Grid>
      </Box>
      <FormContainer data-testid="form-claimsCardInformation">
        <FormFields type="dialog">
          <FormGrid container spacing={6}>
            <FormGrid item xs={12} sm={4}>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormLabel label={utils.string.t('claims.claimInformation.claimRef')} align="left" />
                  <FormLabel
                    label={(claimRefId?.claimReference || claimRefId?.claimRef) ?? utils.string.t('claims.claimInformation.autoGenerated')}
                    nestedClasses={{ root: classes.claimRef }}
                    align="left"
                  />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormDate
                    {...utils.form.getFieldProps(fields, 'fromDate', control)}
                    error={errors.fromDate}
                    muiComponentProps={{
                      disabled: dateValidation,
                      fullWidth: true,
                      classes: {
                        root: classes.datepicker,
                      },
                    }}
                  />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormText {...utils.form.getFieldProps(fields, 'location', control)} error={errors.location} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormRadio {...utils.form.getFieldProps(fields, 'adjuster', control)} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'complexity', control)} error={errors.complexity} />
                </FormGrid>
              </FormGrid>
              {isBordereauChecked && (
                <FormGrid container>
                  <FormGrid item xs={12}>
                    <FormCheckbox
                      {...utils.form.getFieldProps(fields, 'bordereauClaim', control)}
                      error={errors.bordereauClaim}
                      nestedClasses={{ root: classes.checkbox }}
                    />
                  </FormGrid>
                </FormGrid>
              )}
            </FormGrid>
            <FormGrid item xs={12} sm={4}>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormGrid container alignItems="center" spacing={2}>
                        <FormGrid item xs={7}>
                          <FormDate {...utils.form.getFieldProps(fields, 'claimReceivedDate', control)} error={errors.claimReceivedDate} />
                        </FormGrid>
                        <FormGrid item xs={5} nestedClasses={{ root: classes.lossTime }}>
                          <FormText {...utils.form.getFieldProps(fields, 'claimReceivedTime', control)} />
                        </FormGrid>
                      </FormGrid>
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormDate
                        {...utils.form.getFieldProps(fields, 'toDate', control)}
                        error={errors.toDate}
                        muiComponentProps={{
                          disabled: dateValidation,
                          fullWidth: true,
                          classes: {
                            root: classes.datepicker,
                          },
                        }}
                      />
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormText {...utils.form.getFieldProps(fields, 'fgunarrative', control)} error={errors.fgunarrative} />
                    </FormGrid>
                  </FormGrid>
                  <FormGrid container>
                    {watch('adjuster') === 'beAdjuster' ? (
                      <FormGrid item xs={12}>
                        <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'beAdjuster', control)} />
                      </FormGrid>
                    ) : (
                      <FormGrid item xs={12}>
                        <FormText {...utils.form.getFieldProps(fields, 'nonBeAdjuster', control)} error={errors.nonBeAdjuster} />
                      </FormGrid>
                    )}
                  </FormGrid>
                  <FormGrid container>
                    <FormGrid item xs={12}>
                      <FormAutocompleteMui
                        {...utils.form.getFieldProps(fields, 'referral', control)}
                        error={errors.referral}
                        muiComponentProps={{
                          disabled: !refValuesDisable,
                        }}
                        callback={(e, data) => {
                          getRefValues(data);
                        }}
                      />
                    </FormGrid>
                  </FormGrid>
                  {isBordereauChecked && (
                    <FormGrid container>
                      <FormGrid item xs={12}>
                        <FormAutocompleteMui
                          {...utils.form.getFieldProps(fields, 'bordereauPeriod', control)}
                          error={errors.bordereauPeriod}
                        />
                      </FormGrid>
                    </FormGrid>
                  )}
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid item xs={12} sm={4}>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'claimantName', control)} error={errors.claimantName} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'lossQualifierID', control)} error={errors.lossQualifierID} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'priority', control)} error={errors.priority} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormText {...utils.form.getFieldProps(fields, 'adjusterReference', control)} error={errors.adjusterReference} />
                </FormGrid>
              </FormGrid>
              <FormGrid container>
                <FormGrid item xs={12}>
                  <FormAutocompleteMui
                    {...utils.form.getFieldProps(fields, 'rfiResponse', control)}
                    error={errors.rfiResponse}
                    muiComponentProps={{
                      disabled: refResponseDisable,
                    }}
                  />
                </FormGrid>
              </FormGrid>
            </FormGrid>
          </FormGrid>
        </FormFields>
      </FormContainer>
      <Box className={classes.UploadDocumentContainer}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <ClaimsUploadViewSearchDocs
              refData={claimData}
              refIdName={claimData?.claimRef ? 'claimRef' : 'claimReference'} //added temp condition as claimRef and claimReference keys are inconsistent, need to be fixed permanently later
              dmsContext={DMS_CONTEXT_CLAIM}
              documentTypeKey={DMS_DOCUMENT_TYPE_SECTION_KEYS.claims}
              isTabView={false}
              isClaims
              isClaimsUploadDisabled={!claimRefId?.claimRef}
              claimsUploadWarningMsg={!claimRefId?.claimRef ? utils.string.t('claims.claimInformation.dms.fileUploadWarning') : ''}
              claimsSearchDocumentsTxt={utils.string.t('claims.claimInformation.dms.searchDocuments')}
              uploadDocumentsTitle={utils.string.t('claims.claimInformation.dms.uploadDocuments')}
              docList={claimsDocsList}
              parentLossRef={parentLossRef}
            />
          </Grid>
        </Grid>
      </Box>
      <RegisterNewLossFixedBottomBar
        activeStep={activeStep}
        isAllStepsCompleted={isAllStepsCompleted}
        handleBack={() => {
          handleBack(index);
        }}
        handleSave={handleSaveForm}
        handleNextSubmit={handleSubmit(handleSubmitForm)}
        save={true}
      />
    </>
  );
}
