const styles = (theme) => ({
  wrapper: {
    margin: theme.spacing(0, 5, 3, 4),
    paddingTop: theme.spacing(3),
    borderTop: `1px solid ${theme.palette.neutral.light}`,
  },
  claimButton: {
    marginTop: theme.spacing(4),
  },
  title: {
    marginTop: theme.spacing(6),
    paddingBottom: theme.spacing(0.5),
    textTransform: 'uppercase',
    fontWeight: theme.typography.fontWeightBold,
    fontSize: theme.typography.pxToRem(14),
  },
  tableHead: {
    fontWeight: theme.typography.fontWeightBold,
  },
  infoCardContainer: {
    display: "flex",
    flexFlow: "row wrap",
    width: "100%",
    alignContent: "space-around",
  },
  infoCard: {
    width: "30%",
    margin: theme.spacing(1.25),

  },
  infoCardTitle: {
    fontWeight: 600,
    textAlign: 'left',
    margin: theme.spacing(0, 0, 1.25, 0),
    color: 'rgba(0, 0, 0, 0.87)!important',

  },
  infoCardValue: {
    textAlign: 'left',
    margin: theme.spacing(0, 0, 0, 0.25),
    color: 'inherit!important',
    fontWeight: 400,


  },
  underWritingContainer:{
    width:"100%"
  },
  underWritingCardContainer:{
    margin:theme.spacing(3, 0.25),
  }

});

export default styles;
