import React from 'react';

//app
import { Layout, FilterBar, Tooltip } from 'components';
import * as utils from 'utils';
import styles from './DmsWidget.styles';

//mui
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { TreeView, TreeItem } from '@material-ui/lab';
import { Box, Typography, Divider, makeStyles } from "@material-ui/core";
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import DescriptionIcon from '@material-ui/icons/Description';

export default function DmsWidget() {
    const classes = makeStyles(styles, { name: 'DmsWidget' })();

    const searchFields = [
        {
            name: 'query',
            type: 'text',
            placeholder: utils.string.t('dms.search.searchDocuments'),
            defaultValue: '',
            gridSize: { xs: 12 },
            muiComponentProps: {
                autoComplete: 'off'
            },
        },
    ];
    const searchActions = [
        {
            name: 'filter',
            label: utils.string.t('app.searchLabel'),
            handler: (values) => { },
        },
        {
            name: 'reset',
            label: utils.string.t('app.reset'),
            handler: {},
        },
    ];

    //mock data will be remove once we receive actual data fom API

    const data = {
        id: 'root',
        name: 'Loss Reference 47001',
        date: '',
        children: [
            {
                id: '1',
                name: 'Q-Core Primary',
                date: '13 Nov 2021',
            },
            {
                id: '2',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '3',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '4',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '5',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '6',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '7',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '8',
                name: '20170606 Initial Advice',
                date: '22 Nov 2021',
            },
            {
                id: '9',
                name: 'Claim 04063',
                date: '',
                children: [
                    {
                        id: '10',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '11',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '12',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '13',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '14',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '15',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                ],
            },
            {
                id: '16',
                name: 'Claim 5473',
                date: '',
                children: [
                    {
                        id: '17',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '18',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '19',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '20',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '21',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '22',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '23',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '24',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '25',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '26',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '27',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '28',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '29',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '30',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '31',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '32',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '33',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                    {
                        id: '34',
                        name: '20173453 Attorney',
                        date: '12 Sep 2020',
                    },
                ],
            },
        ],
    };

    const renderTree = (nodes) => (
        <TreeItem
            key={nodes.id}
            nodeId={nodes.id}
            label={
                <Box className={classes.rootTree}>
                    <Typography variant="body2" className={classes.nameCol}>
                        <Tooltip
                            title={
                                <Box p={2}>
                                    <Box>
                                        {nodes.name} {`With Contact Info`}
                                    </Box>
                                    <Box mt={2}>{`Doc Type : Claim => Correspondence (Adjustments)`}</Box>
                                    <Box mt={2}>{`Originally Created on :${nodes.date}`}</Box>
                                </Box>
                            }
                            placement={'top'}
                            arrow
                        >
                            {nodes.name}
                        </Tooltip>
                    </Typography>
                    <Typography className={classes.dateColumn} variant="caption" color="inherit">
                        {nodes.date}
                    </Typography>
                </Box>
            }
        >
            {utils.generic.isInvalidOrEmptyArray(nodes.children) ? null : nodes.children.map((node) => renderTree(node))}
        </TreeItem>
    );

    return (
        <Layout sidebar padding={false}>
            <Box pt={2} display="flex" flexDirection="column">
                <Box display="flex" alignItems="center">
                    <DescriptionIcon />
                    <Typography variant="body1">{utils.string.t('dms.view.documents.title')}</Typography>
                </Box>
                <Box pt={2} pl={1} pr={1}>
                    <FilterBar id="userFilter" fields={searchFields} actions={searchActions} />
                    <Divider />
                    <Box display="flex">
                        <Box flex={'0.8'} pl={2}>
                            <Typography variant="body2">{utils.string.t('dms.upload.file')}</Typography>
                        </Box>
                        <Box flex={'0.4'} pl={2}>
                            <Typography variant="body2">{utils.string.t('dms.upload.uploadedOn')}</Typography>
                        </Box>
                    </Box>
                    <Divider />
                    <TreeView
                        aria-label="rich object"
                        defaultCollapseIcon={<ExpandMoreIcon />}
                        defaultExpanded={['1', '9', 'root']}
                        defaultExpandIcon={<ChevronRightIcon />}
                        sx={{ height: 110, flexGrow: 1, maxWidth: 400, overflowY: 'auto' }}
                    >
                        {renderTree(data)}
                    </TreeView>
                </Box>
            </Box>
        </Layout>
    )
}
