import { get } from 'lodash';
import types from './types';
import * as utils from 'utils';

const initialState = {
  view: {
    files: [],
    versionHistory: {},
    fileMetaData: {},
    isDmsFileViewGridDataLoading: false,
    isDmsWidgetDocsLoading: false
  },
  search: {
    files: [],
  },
  upload: {
    documentMetaData: {},
    document: {},
    documentUploaded: {},
  },
  fileUpload: { // TODO will later replace "upload"
    metaData: {
      requestParams: { referenceId: '', sectionRef: '' },
      data: {},
    },
    documentsUploaded: {},
  },
  contextSubType: {
    type: '',
    caseIncidentID: '',
    caseIncidentNotesID: '',
    refId: '',
  },
  dmsWidgetExpanded: false
};

const dmsReducers = (state = initialState, action) => {
  switch (action.type) {
    case 'DMS_DOCUMENT_TYPES_GET_SUCCESS':
      return {
        ...state,
        documentTypes: action.payload,
      };

    case 'DMS_SEARCH_DOCUMENTS_SUCCESS':
      return {
        ...state,
        search: {
          ...state.search,
          files: action.payload,
          itemsTotal: get(action.payload, 'totalElements', 0),
          query: get(action.payload, 'searchBy') || [],
          filters: state?.search?.filters || get(action.payload, 'data.filterValue'),
        },
      };

    case 'DMS_FILTER_DOCUMENTS_SUCCESS':
      return {
        ...state,
        search: {
          ...state.search,
          files: state?.search?.files || action.payload,
          query: get(action.payload, 'searchBy') || [],
          filters: Object.keys(action.payload?.data?.filterValue).reduce((options, field) => {
            options[field] = action.payload.data.filterValue[field].sort((a, b) => (utils.string.capitalise(a.name) > utils.string.capitalise(b.name) ? 1 : -1));
            return options;
          }, {}),
        },
      };

    case 'DMS_SEARCH_DOCUMENTS_RESET':
      return {
        ...state,
        search: initialState.search,
      };

    case types.GET_DMS_DOCUMENT_PENDING:
      return {
        ...state,
        upload: {
          ...state.upload,
          document: {
            status: 'PENDING',
            data: {},
            error: null,
          },
        },
      };

    case types.GET_DMS_DOCUMENT_SUCCESS:
      return {
        ...state,
        upload: {
          ...state.upload,
          document: {
            status: 'SUCCESS',
            data: action.payload,
            error: false,
          },
        },
      };

    case types.GET_DMS_DOCUMENT_REJECTED:
      return {
        ...state,
        upload: {
          ...state.upload,
          document: {
            status: 'REJECTED',
            error: action.payload,
          },
        },
      };

    case types.UPDATE_DMS_UPLOAD_DOCUMENT_DTO:
      return {
        ...state,
        upload: {
          ...state.upload,
          documentUploaded: {
            ...state.upload?.documentUploaded,
            data: {
              ...state.upload?.documentUploaded?.data,
              documentDto: [...state.upload?.documentUploaded?.data?.documentDto?.filter((item) => item.documentName !== action.payload)],
            },
          },
        },
      };

    case types.RESET_DMS_UPLOAD_STATUS:
      return {
        ...state,
        upload: {
          ...state.upload,
          documentUploaded: {
            ...state.upload?.documentUploaded,
            status: '',
          },
        },
      };

    case 'DMS_UPLOAD_DOCUMENTS_RESET':
      return {
        ...state,
        upload: {
          ...initialState.upload,
        },
      };
    case 'DMS_VIEW_TABLE_DOCUMENTS_REQUEST':
      return {
        ...state,
        view: {
          ...state.view,
          isDmsFileViewGridDataLoading: true,
        },
      };
    case 'DMS_WIDGET_DOCUMENT_REQUEST':
      return {
        ...state,
        view: {
          ...state.view,
          isDmsWidgetDocsLoading: true
        }
      }
    case 'DMS_VIEW_TABLE_DOCUMENTS_SUCCESS':
      return {
        ...state,
        view: {
          ...state.view,
          files: action.payload,
          isDmsFileViewGridDataLoading: false,
          [action.payload?.[0]?.referenceId]: action.payload
        },
      };
    case 'DMS_WIDGET_DOCUMENTS_SUCCESS':
      return {
        ...state,
        view: {
          ...state.view,
          isDmsWidgetDocsLoading: false,
          [action.payload[0]?.referenceId]: action.payload
        },
      };
    case 'DMS_VIEW_TABLE_DOCUMENTS_FAILURE':
      return {
        ...state,
        view: {
          ...state.view,
          isDmsFileViewGridDataLoading: false,
        },
      };
      case 'DMS_WIDGET_DOCUMENTS_FAILURE':
        return {
          ...state,
          view: {
            ...state.view,
            isDmsWidgetDocsLoading: false
          },
        };
    case 'DMS_VIEW_DOCUMENTS_RESET':
      return {
        ...state,
        view: {
          ...initialState.view,
        },
      };

    case types.GET_DMS_VERSION_HISTORY_SUCCESS:
      return {
        ...state,
        view: {
          ...state.view,
          versionHistory: action.payload,
        },
      };

    case 'DMS_HISTORY_DOCUMENTS_RESET':
      return {
        ...state,
        view: {
          ...state.view,
          versionHistory: initialState.view.versionHistory,
        },
      };

    case 'GET_EDIT_DMS_META_DATA_SUCCESS':
      return {
        ...state,
        view: {
          ...state.view,
          fileMetaData: action.payload,
        },
      };
    case 'DMS_EDIT_META_DATA_RESET':
      return {
        ...state,
        view: {
          ...state.view,
          fileMetaData: initialState.view.fileMetaData,
        },
      };

    case 'DMS_METADATA_GET_REQUEST':
      const { referenceId, sectionRef } = action.payload;
      return {
        ...state,
        fileUpload: {
          ...state.fileUpload,
          metaData: {
            ...state.fileUpload.metaData,
            requestParams: { referenceId: referenceId, sectionRef: sectionRef },
            data: initialState.fileUpload.metaData.data,
          },
        },
      };
    case 'DMS_METADATA_GET_SUCCESS':
      return {
        ...state,
        fileUpload: {
          ...state.fileUpload,
          metaData: { ...state.fileUpload.metaData, data: action.payload },
        },
      };
    case 'DMS_METADATA_GET_ERROR':
      return {
        ...state,
        fileUpload: { ...state.fileUpload, metaData: { ...initialState.fileUpload.metaData } },
      };

    case 'DMS_POST_DOCUMENTS_REQUEST':
      return {
        ...state,
        fileUpload: { ...state.fileUpload, documentsUploaded: initialState.fileUpload.documentsUploaded },
      };

    case 'DMS_POST_DOCUMENTS_SUCCESS':
      return {
        ...state,
        fileUpload: { ...state.fileUpload, documentsUploaded: action.payload },
      };

    case 'DMS_POST_DOCUMENTS_ERROR':
      return {
        ...state,
        fileUpload: { ...state.fileUpload, documentsUploaded: initialState.fileUpload.documentsUploaded },
      };

    case 'DMS_TASK_CONTEXT_TYPE_SET':
      return {
        ...state,
        contextSubType: {
          ...state.contextSubType,
          ...action.payload,
        },
      };

    case 'DMS_TASK_CONTEXT_TYPE_RESET':
      return {
        ...state,
        contextSubType: initialState.contextSubType,
      };

    case 'DMS_WIDGET_EXPANDED':
      return {
        ...state,
        dmsWidgetExpanded: action.payload
      };

    default:
      return state;
  }
};

export default dmsReducers;
