import React, { useState } from 'react';
import { useParams } from 'react-router';
import { useSelector } from 'react-redux';

// app
import { LossDashboardView } from './LossDashboard.view';
import { selectLossSelected, selectFnolSelectedTab } from 'stores';
import * as utils from 'utils';
import config from 'config';

export default function LossDashboard() {
  const params = useParams();

  const lossSelected = useSelector(selectLossSelected);
  const fnolSelectedTab = useSelector(selectFnolSelectedTab);

  const [selectedTab, setSelectedTab] = useState(params?.tab || 'lossDetail');

  const handleSelectTab = (tabName) => {
    setSelectedTab(tabName);
  };

  const breadcrumbs = [
    {
      name: 'claimsFNOL',
      label: utils.string.t('loss.title'),
      link: `${config.routes.claimsFNOL.root}${fnolSelectedTab ? `/tab/${fnolSelectedTab}` : ''}`,
    },
    {
      name: 'loss',
      label: `${utils.string.t('loss.text', {
        lossRef: lossSelected?.lossRef,
      })}`,
      link: `${config.routes.claimsFNOL.loss}/${lossSelected?.lossRef}`,
      active: true,
    },
  ];

  const tabs = [
    { value: 'lossDetail', label: utils.string.t('loss.tabMenu.details') },
    { value: 'lossAction', label: utils.string.t('loss.tabMenu.actions') },
    { value: 'lossDocs', label: utils.string.t('loss.tabMenu.docs') },
    { value: 'lossNotes', label: utils.string.t('loss.tabMenu.notes') },
    { value: 'lossAuditTrail', label: utils.string.t('loss.tabMenu.auditTrail') },
  ];

  // abort
  if (!utils.generic.isValidObject(lossSelected)) return null;

  return (
    <LossDashboardView
      tabs={tabs}
      selectedTab={selectedTab}
      lossSelected={lossSelected}
      handleSelectTab={handleSelectTab}
      breadcrumbs={breadcrumbs}
    />
  );
}
