import React, {  useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import * as Yup from 'yup';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';

// app
import styles from './ClaimsTab.styles';
import {
  resetClaimProcessingFilters,
  resetClaimsProcessingSearch,
  selectUser,
  updateClaimProcessingFilters,
  selectClaimsTabData,
  getClaimsTabData,
  getClaimsTabDetails
} from 'stores';
import * as constants from 'consts';
import * as utils from 'utils';
import { useFlexiColumns } from 'hooks';
import { MultiSelect, FormDate } from 'components';
import ClaimsTabView from './ClaimsTab.view';

// mui
import { makeStyles } from '@material-ui/core';

export default function ClaimsTab(){
  const classes = makeStyles(styles, { name: 'ClaimsManagement' })();
  const dispatch = useDispatch();

  const user = useSelector(selectUser);
  const userHasMyClaimsPermission = utils.app.access.feature('claimsProcessing.myClaims', ['read', 'create', 'update'], user);
  const userHasMyTeamClaimsPermission = utils.app.access.feature('claimsProcessing.myTeamClaims', ['read', 'create', 'update'], user);

  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState(constants.CLAIMS_SEARCH_OPTION_INSURED);

  const [searchByText, setSearchByText] = useState(utils.string.t('claims.searchByClaims.options.ClaimRef'));
  const [isClosedClaimsEnabled, setIsClosedClaimsEnabled] = useState(false);

  const [myClaimsFirstTimeLoad, setMyClaimsFirstTimeLoad] = useState(true);
  const [myTeamClaimsFirstTimeLoad, setMyTeamClaimsFirstTimeLoad] = useState(true);
  const firstTimeSortColumns = 'createdDate, priority';

  const searchTypeCall = constants.CLAIM_PROCESSING_REQ_TYPES.search;
  const filterTypeCall = constants.CLAIM_PROCESSING_REQ_TYPES.filter;
  const [resetKey, setResetKey] = useState();

  const claimsTabData = useSelector(selectClaimsTabData);
  const claimsTabTableRowDetails = claimsTabData?.tableDetails?.items;
  const claimsTabFilterDropDown = claimsTabData?.tableDetails?.filters;
  const isFetchingFilters = claimsTabData?.tableDetails?.isloadingFilters;
  
  const [claimsType, setClaimsType] = useState(claimsTabData?.view || constants.CLAIM_TEAM_TYPE.myClaims);

  const selectOptions = [
    { label: utils.string.t('claims.searchByClaims.options.Insured'), value: constants.CLAIMS_SEARCH_OPTION_INSURED },
    { label: utils.string.t('claims.searchByClaims.options.ClaimRef'), value: constants.CLAIMS_SEARCH_OPTION_CLAIM_REF },
    { label: utils.string.t('claims.searchByClaims.options.LossRef'), value: constants.CLAIMS_SEARCH_OPTION_LOSS_REF },
    { label: utils.string.t('claims.searchByClaims.options.PolicyRef'), value: constants.CLAIMS_SEARCH_OPTION_POLICY_REF },
    { label: utils.string.t('claims.searchByClaims.options.Division'), value: constants.CLAIMS_SEARCH_OPTION_DIVISION }

  ];

  const viewFields = [
    {
          name: 'views',
          type: 'radio',
          value: claimsType,
          defaultValue: claimsType,
          muiFormGroupProps: {
            row: true,
            nestedClasses: { root: classes.adjusterRadioGroup },
            classes: {
              root: classes.radioLabel,
            },
            onChange: (value) => {
              setClaimsType(value);
              setValue('includeClosedClaims', false);
              setIsClosedClaimsEnabled(false);
            },
          },
          options: [
            ...(userHasMyClaimsPermission
              ? [
                  {
                    value: constants.CLAIM_TEAM_TYPE.myClaims,
                    label: utils.string.t('claims.processing.myClaims'),
                  },
                ]
              : []),
            ...(userHasMyTeamClaimsPermission
              ? [
                  {
                    value: constants.CLAIM_TEAM_TYPE.myTeamClaims,
                    label: utils.string.t('claims.processing.myTeamClaims'),
                  },
                ]
              : [])
          ],
        },
        {
          name: 'searchBy',
          type: 'autocompletemui',
          options: selectOptions,
          value: (claimsTabData?.searchBy === '') ?
                 (selectOptions?.find((item) => item?.value === constants.CLAIMS_SEARCH_OPTION_INSURED) || null) :
                 (selectOptions?.find((item) => item?.value === claimsTabData?.searchBy)),
          validation: Yup.object().nullable().required(utils.string.t('validation.required')),
          muiComponentProps: {
            inputProps: {
              title: searchByText || '',
            },
          },
        },
        { name: 'createdDate', type: 'datepicker', value: null },
        {
          name: 'includeClosedClaims',
          type: 'switch',
          value: false,
          muiComponentProps: {
            onChange: (name, checked) => {
              setIsClosedClaimsEnabled(checked);
            },
          },
        },
      ];

    const defaultValues = utils.form.getInitialValues(viewFields);
    const validationSchema = utils.form.getValidationSchema(viewFields);

    const { control, setValue } = useForm({
        defaultValues,
        ...(validationSchema && { resolver: yupResolver(validationSchema) }),
      });

    const tableFilterFields = [
        {
          id: 'claimStage',
          type: 'multiSelect',
          label: utils.string.t('claims.columns.claimsList.claimStage'),
          placeholder: utils.string.t('claims.filterPlaceHolderText'),
          value: [],
          options: claimsTabFilterDropDown?.team,
          content: <MultiSelect id="claimStage" search options={claimsTabFilterDropDown?.team} />,
        },
        {
          id: 'lossFromDate',
          type: 'datepicker',
          label: utils.string.t('claims.columns.claimsList.lossFromDate'),
          value: '',
          content: (
            <FormDate
              control={control}
              {...utils.form.getFieldProps(defaultValues, 'lossFromDate')}
              id="lossFromDatedatepicker"
              name="lossFromDate"
              type="datepicker"
              value={''}
              label={''}
              plainText
              plainTextIcon
              placeholder={utils.string.t('app.selectDate')}
              muiComponentProps={{
                fullWidth: false,
                margin: 'dense',
              }}
              muiPickerProps={{
                clearable: false,
                variant: 'inline',
                format: 'DD-MM-YYYY',
              }}
            />
          ),
        },
        {
          id: 'insured',
          type: 'multiSelect',
          label: utils.string.t('claims.columns.claimsList.insured'),
          placeholder: utils.string.t('claims.filterPlaceHolderText'),
          value: [],
          options: claimsTabFilterDropDown?.team,
          content: <MultiSelect id="team" search options={claimsTabFilterDropDown?.team} />,
        },
        ...(claimsType === constants.CLAIM_TEAM_TYPE.myTeamClaims
          ? [
              {
                id: 'assignedTo',
                type: 'multiSelect',
                label: utils.string.t('claims.columns.claimsList.assignedTo'),
                value: [],
                options: claimsTabFilterDropDown?.assignedTo,
                nestedClasses: { root: classes.fieldWidth },
                content: <MultiSelect id="assignedTo" search options={claimsTabFilterDropDown?.assignedTo} />,
              },
            ]
          : []),
          {
            id: 'lastUpdated',
            type: 'datepicker',
            label: utils.string.t('claims.columns.claimsList.lastUpdated'),
            value: '',
            content: (
              <FormDate
                control={control}
                {...utils.form.getFieldProps(defaultValues, 'lastUpdated')}
                id="lastUpdateddatepicker"
                name="lastUpdated"
                type="datepicker"
                value={''}
                label={''}
                plainText
                plainTextIcon
                placeholder={utils.string.t('app.selectDate')}
                muiComponentProps={{
                  fullWidth: false,
                  margin: 'dense',
                }}
                muiPickerProps={{
                  clearable: false,
                  variant: 'inline',
                  format: 'DD-MM-YYYY',
                }}
              />
            ),
          },
        {
          id: 'priority',
          type: 'multiSelect',
          label: utils.string.t('claims.claimInformation.priority'),
          placeholder: utils.string.t('claims.filterPlaceHolderText'),
          value: [],
          options: claimsTabFilterDropDown?.priority,
          content: <MultiSelect id="priority" search options={claimsTabFilterDropDown?.priority} />,
        },
        {
          id: 'division',
          type: 'multiSelect',
          label: utils.string.t('claims.columns.claimsList.division'),
          placeholder: utils.string.t('claims.filterPlaceHolderText'),
          value: [],
          options: claimsTabFilterDropDown?.processState,
          content: <MultiSelect id="division" search options={claimsTabFilterDropDown?.processState} />,
        }
      ];

    useEffect(() => {
        if(!claimsTabData?.isClaimsTabLoaded) {
            getClaimsData();
            const claimsData = {...claimsTabData};
            claimsData.isClaimsTabLoaded = true;
            dispatch(getClaimsTabData(claimsData));
        } 
        else if (claimsTabData?.isClaimsTabLoaded && (claimsTabData?.view !== claimsType || claimsTabData?.includedCloseClaims !== isClosedClaimsEnabled)) {
          getClaimsData();
          const claimsData = {...claimsTabData};
          claimsData.view = claimsType;
          claimsData.includedCloseClaims = isClosedClaimsEnabled;
          dispatch(getClaimsTabData(claimsData));
        } 
        // else if (claimsTabData?.isClaimsTabLoaded && claimsTabData?.includedCloseClaims !== isClosedClaimsEnabled) {
        //   getClaimsData();
        //   const claimsData = {...claimsTabData};
        //   claimsData.includedCloseClaims = isClosedClaimsEnabled;
        //   dispatch(getClaimsTabData(claimsData));
        // }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [claimsType, isClosedClaimsEnabled]);

    const getClaimsData = () => {
        if (claimsType) {
            setValue('createdDate', null);
      
            if (claimsType === constants.CLAIM_TEAM_TYPE.allClaims) {
              setIsClosedClaimsEnabled(false);
            }
      
            // defect - 14469 - Default sorting is not applying on Date &Time created and Priority columns
            // First Time Table Grid load for MyClaims, MyTeamClaims, All Claims passing createdDate and Priority columns from UI.
            if (
              (myClaimsFirstTimeLoad && claimsType === constants.CLAIM_TEAM_TYPE.myClaims) ||
              (myTeamClaimsFirstTimeLoad && claimsType === constants.CLAIM_TEAM_TYPE.myTeamClaims)
            ) {
              if (claimsType === constants.CLAIM_TEAM_TYPE.myClaims) setMyClaimsFirstTimeLoad(!myClaimsFirstTimeLoad);
              if (claimsType === constants.CLAIM_TEAM_TYPE.myTeamClaims) setMyTeamClaimsFirstTimeLoad(!myTeamClaimsFirstTimeLoad);
      
              dispatch(
                getClaimsTabDetails({
                  requestType: searchTypeCall,
                  claimsType,
                  filterTerm: [],
                  searchBy,
                  pullClosedRecords: isClosedClaimsEnabled,
                  firstTimeSort: firstTimeSortColumns,
                })
              );
              dispatch(
                getClaimsTabDetails({
                  requestType: filterTypeCall,
                  claimsType,
                  filterTerm: [],
                  searchBy,
                  pullClosedRecords: isClosedClaimsEnabled,
                  firstTimeSort: firstTimeSortColumns,
                })
              );
            } else {
              // From second time onwards, table grid load from else part for single column sorting.
              dispatch(
                getClaimsTabDetails({
                  requestType: searchTypeCall,
                  claimsType,
                  filterTerm: [],
                  searchBy,
                  pullClosedRecords: isClosedClaimsEnabled,
                })
              );
              dispatch(
                getClaimsTabDetails({
                  requestType: filterTypeCall,
                  claimsType,
                  filterTerm: [],
                  searchBy,
                  pullClosedRecords: isClosedClaimsEnabled,
                })
              );
            }
          }  
    }
    

    const handleSearch = (queryObject) => {
        setResetKey(new Date().getTime());
        dispatch(resetClaimProcessingFilters());
        setValue('createdDate', null);
        if (queryObject?.search) {
          setSearchTerm(queryObject.search);
          const claimsData = {...claimsTabData};
          claimsData.searchText = queryObject?.search;
          dispatch(getClaimsTabData(claimsData));
          dispatch(
            getClaimsTabDetails({
              requestType: searchTypeCall,
              claimsType,
              term: queryObject.search,
              direction: 'desc',
              searchBy,
              pullClosedRecords: isClosedClaimsEnabled,
            })
          );
          dispatch(
            getClaimsTabDetails({
              requestType: filterTypeCall,
              claimsType,
              term: queryObject.search,
              direction: 'desc',
              searchBy,
              filterTerm: queryObject.filters,
              pullClosedRecords: isClosedClaimsEnabled,
            })
          );
        }
      };

    const handleResetFilter = () => {
        setValue('createdDate', null);
        dispatch(resetClaimProcessingFilters());
        dispatch(
          getClaimsTabDetails({
            requestType: searchTypeCall,
            claimsType,
            term: searchTerm,
            filterTerm: '',
            direction: 'desc',
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
        dispatch(
          getClaimsTabDetails({
            requestType: filterTypeCall,
            claimsType,
            term: searchTerm,
            filterTerm: '',
            direction: 'desc',
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const handleSearchFilter = (data) => {
        dispatch(
          updateClaimProcessingFilters({ claimsType, search: data?.search, filters: data.filters, pullClosedRecords: isClosedClaimsEnabled })
        );
        return dispatch(
          getClaimsTabDetails({
            requestType: searchTypeCall,
            claimsType,
            term: data.search,
            filterTerm: data.filters,
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const handleSort = (by, dir) => {
        dispatch(
          getClaimsTabDetails({
            requestType: searchTypeCall,
            claimsType,
            sortBy: by,
            direction: dir.toUpperCase(),
            term: searchTerm,
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const handleChangePage = (newPage) => {
        dispatch(
          getClaimsTabDetails({
            requestType: searchTypeCall,
            claimsType,
            page: newPage,
            term: searchTerm,
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const handleChangeRowsPerPage = (rowsPerPage) => {
        dispatch(
          getClaimsTabDetails({
            requestType: searchTypeCall,
            claimsType,
            size: rowsPerPage,
            term: searchTerm,
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const handleResetSearch = () => {
        setResetKey(new Date().getTime());
        dispatch(resetClaimsProcessingSearch());
        dispatch(resetClaimProcessingFilters());
        setValue('createdDate', null);
        dispatch(
          getClaimsTabDetails({ requestType: searchTypeCall, claimsType, filterTerm: [], searchBy, pullClosedRecords: isClosedClaimsEnabled })
        );
        dispatch(
          getClaimsTabDetails({
            requestType: filterTypeCall,
            claimsType,
            filterTerm: [],
            searchBy,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const onSelectSearchBy = (searchByValue, searchByLabel) => {
        setSearchByText(searchByLabel);
        setSearchBy(searchByValue);
        const claimsData = {...claimsTabData};
        claimsData.searchBy = searchByValue;
        dispatch(getClaimsTabData(claimsData));
        dispatch(
          getClaimsTabDetails({
            requestType: searchTypeCall,
            claimsType,
            searchBy: searchByValue,
            term: searchTerm,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
        dispatch(
          getClaimsTabDetails({
            requestType: filterTypeCall,
            claimsType,
            searchBy: searchByValue,
            term: searchTerm,
            pullClosedRecords: isClosedClaimsEnabled,
          })
        );
      };

    const columns = [
        {
          id: 'claimRef',
          label: utils.string.t('claims.claimsTab.tablecolumns.claimRef'),
          sort: { type: 'lexical', direction: 'asc' },
          narrow: true,
          nowrap: true,
          visible: true,
          mandatory: true,
        },
        {
          id: 'lossRef',
          label: utils.string.t('claims.claimsTab.tablecolumns.lossRef'),
          sort: { type: 'lexical', direction: 'asc' },
          narrow: true,
          nowrap: true,
          visible: true,
          mandatory: true,
        },
        {
          id: 'lossDateFrom',
          label: utils.string.t('claims.claimsTab.tablecolumns.lossDateFrom'),
          sort: { type: 'lexical', direction: 'asc' },
          ellipsis: true,
          visible: true,
        },
        {
          id: 'assured',
          label: utils.string.t('claims.claimsTab.tablecolumns.insured'),
          sort: { type: 'lexical', direction: 'asc' },
          ellipsis: true,
          visible: true,
        },
        {
          id: 'policyRef',
          label: utils.string.t('claims.claimsTab.tablecolumns.policyRef'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: true,
        },
        {
          id: 'division',
          label: utils.string.t('claims.claimsTab.tablecolumns.division'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: true
        },
        {
          id: 'processState',
          label: utils.string.t('claims.claimsTab.tablecolumns.claimStatus'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: true,
        },
        {
          id: 'claimStage',
          label: utils.string.t('claims.claimsTab.tablecolumns.claimStage'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: true,
        },
        {
          id: 'assignedTo',
          label: utils.string.t('claims.claimsTab.tablecolumns.assignedTo'),
          sort: { type: 'lexical', direction: 'asc' },
          visible: true,
          mandatory: true,
          nowrap: true,
        },
        {
          id: 'team',
          label: utils.string.t('claims.claimsTab.tablecolumns.team'),
          sort: { type: 'lexical', direction: 'asc' },
          visible: true,
          mandatory: true,
        },
        {
          id: 'lastUpdated',
          label: utils.string.t('claims.claimsTab.tablecolumns.lastUpdated'),
          sort: { type: 'lexical', direction: 'asc' },
          visible: true,
          mandatory: true,
        },
        {
          id: 'priority',
          label: utils.string.t('claims.claimsTab.tablecolumns.priority'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: true,
        },
        {
          id: 'catCodesID',
          label: utils.string.t('claims.claimsTab.tablecolumns.catCode'),
          sort: { type: 'lexical', direction: 'asc' },
          ellipsis: true
        },
        {
          id: 'claimReceivedDateTime',
          label: utils.string.t('claims.claimsTab.tablecolumns.claimReceivedDateTime'),
          sort: { type: 'lexical', direction: 'asc' },
          narrow: true,
          nowrap: true,
        },
        {
          id: 'createdDate',
          label: utils.string.t('claims.claimsTab.tablecolumns.createdDate'),
          sort: { type: 'date', direction: 'asc' },
          nowrap: true,
          visible: false,
        },
        {
          id: 'closedDate',
          label: utils.string.t('claims.claimsTab.tablecolumns.closedDate'),
          sort: { type: 'date', direction: 'asc' },
          nowrap: true,
        },
        {
          id: 'complexity',
          label: utils.string.t('claims.claimsTab.tablecolumns.complexity'),
          sort: { type: 'lexical', direction: 'asc' },
          visible: false,
        },
        {
          id: 'ucr',
          label: utils.string.t('claims.claimsTab.tablecolumns.ucr'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: false,
        },
        {
          id: 'policyType',
          label: utils.string.t('claims.claimsTab.tablecolumns.policyType'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: false,
        },
        {
          id: 'claimantName',
          label: utils.string.t('claims.claimsTab.tablecolumns.claimant'),
          sort: { type: 'lexical', direction: 'asc' },
          visible: false,
          ellipsis: true,
        },
        {
          id: 'reinsured',
          label: utils.string.t('claims.claimsTab.tablecolumns.reinsured'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: false
        },
        {
          id: 'client',
          label: utils.string.t('claims.claimsTab.tablecolumns.client'),
          sort: { type: 'lexical', direction: 'asc' },
        },
        {
          id: 'interest',
          label: utils.string.t('claims.claimsTab.tablecolumns.interest'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: false,
        },
        {
          id: 'createdBy',
          label: utils.string.t('claims.claimsTab.tablecolumns.createdBy'),
          sort: { type: 'numeric', direction: 'asc' },
          nowrap: true,
          visible: false,
        },
        {
          id: 'company',
          label: utils.string.t('claims.claimsTab.tablecolumns.company'),
          sort: { type: 'lexical', direction: 'asc' },
        },
        {
          id: 'coverholder',
          label: utils.string.t('claims.claimsTab.tablecolumns.coverholder'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          visible: false,
        },
        {
          id: 'lossToDate',
          label: utils.string.t('claims.claimsTab.tablecolumns.lossDateTo'),
          sort: { type: 'date', direction: 'asc' },
          nowrap: true,
        },
        {
          id: 'lossDateQualifier',
          label: utils.string.t('claims.claimsTab.tablecolumns.lossDateQualifier'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
        },
        {
          id: 'lossDetails',
          label: utils.string.t('claims.claimsTab.tablecolumns.lossDetails'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
          ellipsis: true,
        },
        {
          id: 'pasClaimRef',
          label: utils.string.t('claims.claimsTab.tablecolumns.pasClaimRef'),
          sort: { type: 'numeric', direction: 'asc' },
          nowrap: true,
        },
    
        {
          id: 'pasStatus',
          label: utils.string.t('claims.claimsTab.tablecolumns.pasStatus'),
          sort: { type: 'lexical', direction: 'asc' },
          nowrap: true,
        }
      ];

    const { columns: columnsArray, columnProps, toggleColumn } = useFlexiColumns(columns);

    return (
        <ClaimsTabView 
            claims={claimsTabTableRowDetails}
            searchTerm = {claimsTabData?.searchText || ''}
            sort={{
              ...claimsTabData?.tableDetails?.sort,
            }}
            columnsArray={columnsArray}
            columnProps={columnProps}
            viewFields={viewFields}
            tableFilterFields={tableFilterFields}
            isFetchingFilters={isFetchingFilters}
            claimsType={'myClaims'}
            control={control}
            resetKey={resetKey}
            handlers={{
              search: handleSearch,
              searchFilter: handleSearchFilter,
              resetFilter: handleResetFilter,
              sort: handleSort,
              changePage: handleChangePage,
              changeRowsPerPage: handleChangeRowsPerPage,
              toggleColumn,
              resetSearch: handleResetSearch,
              onSelectSearchBy
            }}
        />
    );
};
