const styles = (theme) => ({
  root: {
    boxShadow: 'none',
  },
  formContainer: {
    marginBottom: theme.spacing(1),
    marginTop: 0,
  },
  accordionTop: {
    borderBottom: `3px dotted ${theme.palette.neutral.light}`,
  },
  title: {
    marginTop: theme.spacing(6),
    padding: theme.spacing(1, 0),
    textTransform: 'uppercase',
    fontWeight: theme.typography.fontWeightBold,
    fontSize: theme.typography.pxToRem(14),
  },
  ugListControls: {
    padding: theme.spacing(0, 1),
    flex: '1',
    display: 'flex',
    maxWidth: theme.spacing(82),
    marginLeft: 'auto',
    '& button': {
      float: 'right',
      height: theme.spacing(5.5),
    },
  },
  ugControlBtn: {
    padding: theme.spacing(0, 1),
    flex: '1',
    display: 'flex',
    maxWidth: theme.spacing(3.8),
    marginLeft: 'auto',
    '& button': {
      float: 'right',
      height: theme.spacing(5.5),
    },
  },
  ugSelectLabel: {
    padding: theme.spacing(1.5, 0.5),
    marginLeft: theme.spacing(4.9),
  },
  ugSelectField: {
    margin: `0 ${theme.spacing(0.9)}px`,
    maxWidth: '80%',
    '& .MuiOutlinedInput-input': {
      width: '286px',
    },
  },
  tableWrap: {
    margin: theme.spacing(2),
    marginTop: 0,
  },
  tableHead: {
    '& th': {
      fontWeight: theme.typography.fontWeightBold,
      fontSize: theme.typography.pxToRem(12),
      borderBottom: 'none',
    },
  },
});

export default styles;
