import { TableRowHeader } from 'modules';
import { LossActionRow } from './LossActionRow';
import { LossActionLevelThree } from './LossActionLevelThree';
import React, {useState} from 'react';

export function LossActionLevelTwo({data, columnProps}){
    const [isOpen, setIsOpen] = useState(false);

    
    return(
        <>
            <LossActionRow
              columnProps={columnProps}
              data={data}
              padding={0}
              color="violet"
              isOpen={isOpen}
              onClick={onClick}
            />
            {data.actionChildItemList.map((itemLevelTwo, index) => {
                return itemLevelTwo.isClaim 
              ?
              <TableRowHeader 
                title={`Claim ${itemLevelTwo.claimRef}`}
                subtitle="International Energy - Plant, Machinery and Equipment - Terraseis 1040 Limited"
                isOpen={isOpen}
                onClick={() => setIsOpen(!isOpen)}
                padding={25}
                color='yellow'
              >
                {isOpen && itemLevelTwo.actionChildItemList.map((itemLevelThree, index) => {
                  return(
                    <>
                     <LossActionLevelThree 
                        data={itemLevelThree}
                        columnProps={columnProps}
                     />
                    </>
                  )
                })}
              </TableRowHeader>
              :
              <LossActionRow
                columnProps={columnProps}
                data={itemLevelTwo}
                padding={25}
                color='yellow'
              />
              
          })}
        </>
)}
