import React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import isString from 'lodash/isString';

// app
import styles from './DetailsCard.styles';
import { Link, Skeleton } from 'components';
// mui
import { makeStyles, Typography } from '@material-ui/core';

DetailCardView.propTypes = {
  title: PropTypes.node,
  details: PropTypes.string,
  textAlign: PropTypes.string,
  link: PropTypes.bool,
  handleClick: PropTypes.func,
  nestedClasses: PropTypes.shape({
    root: PropTypes.string,
    title: PropTypes.string,
    details: PropTypes.string,
  }),
};

export function DetailCardView({ title, details, isLoading, textAlign, nestedClasses, link, handleClick, width }) {
  const classes = makeStyles(styles, { name: 'DetailCard' })({ textAlign, width });
  return (
    <div className={classnames(classes.root, nestedClasses.root)}>
      <Typography className={classes.title} variant="subtitle2" color="secondary" title={isString(title) ? title : null} >
        {title}
      </Typography>
      <Typography className={classes.details} variant="body2" title={details ? details : null}>
        {isLoading ? <Skeleton height={40} animation="wave" displayNumber={1} /> :
          link === true ?
            <Link title={link}
              text={"link"}
              color="secondary"
              nestedClasses={{ link: classes.link }}
              handleClick={handleClick} />
            :
            details}
      </Typography>
    </div>
  );
}
