const styles = (theme) => ({
  row: {
    height: (props) => (props.expanded ? 'auto' : 0),
    transitionDuration: '194ms',
    transition: 'height 300ms cubic-bezier(0.4, 0, 0.2, 1) 0ms'
  },
});

export default styles;
