import React from 'react';
import PropTypes from 'prop-types';

// app
import styles from './TableRowHeader.styles';
import { TableCell } from 'components';

// mui
import { makeStyles, Box, TableRow, Typography, Accordion, AccordionDetails, AccordionSummary } from '@material-ui/core';
import OpenInNewOutlinedIcon from '@material-ui/icons/OpenInNewOutlined';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

TableRowHeaderView.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string.isRequired,
  expanded: PropTypes.bool,
  children: PropTypes.oneOfType([PropTypes.arrayOf(PropTypes.node), PropTypes.node]).isRequired,
};

export function TableRowHeaderView({
  title,
  subtitle,
  expanded, 
  children
}) {
  const classes = makeStyles(styles, { name: 'TableRowHeader' })();

  return (
    
     <>
        <TableRow className={classes.wrapper}>
          <TableCell colspan="100%">
            <Accordion expanded={expanded} data-testid={`accordion${title}`} disableGutters={true} className={classes.panel}>
              <AccordionSummary disableGutters={true}>
                <Box>
                  <KeyboardArrowDownIcon className={classes.arrow} />
                  <Typography variant="h2" className={classes.title}>
                    {title}
                  </Typography>
                  <Typography variant="p" color="textSecondary">
                    {subtitle}
                  </Typography>
                  <OpenInNewOutlinedIcon classes={{ root: classes.openInNewOutIcon }} />
                </Box>
              </AccordionSummary>
  
            </Accordion>
          </TableCell>
        </TableRow>
        <AccordionDetails>{children}</AccordionDetails>

</>
  );
}
