const styles = (theme) => ({
    wrapper: {
        cursor: 'pointer',
        position: 'relative'
    },
    title: {
        marginBottom: theme.spacing(0.25),
    },
    panel: {
        paddingLeft: theme.spacing(3),
    },
    openInNewOutIcon: {
        position: 'absolute',
        right: 0,
        top: 0,
        bottom: 0,
        margin: 'auto'
    },
    arrow: {
        position: 'absolute',
        left: 0,
        top: 0,
        bottom: 0,
        margin: 'auto',
        flex: '0 0 auto',
        verticalAlign: 'middle',
        transform: (props) =>(props.expanded ? 'rotate(0)' : 'rotate(-90deg)'),
       //  (props.expanded ? 'rotate(-90deg)' : 'rotate(0)')   {console.log(props)}
    },
});

export default styles;
