const styles = (theme) => ({

/*
  row: {
    visibility: (props) => (props.expanded ? 'visible' : 'collapse'),
    height: (props) => (props.expanded ? 'auto' : 0),
    transition: 'height 1s ease-out;'
  },
*/
row: {
  display: (props) => (props.expanded ? 'table-row' : 'none'),
  ...theme.mixins.highlight.default,
},

});

export default styles;
